import React, { useState, useEffect } from "react";
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Paper,
  TextField,
} from "@mui/material";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function StatusDebtor() {
  const [participants, setParticipants] = useState([]);
  const [splitPrices, setSplitPrices] = useState({});
  const [payments, setPayments] = useState({});

  useEffect(() => {
    // Fetch data from localStorage
    const storedParticipants =
      JSON.parse(localStorage.getItem("participants")) || [];
    const storedSplitPrices = JSON.parse(localStorage.getItem("splitPrices")) || {}; // ดึง splitPrices จาก localStorage
    const storedPayments = JSON.parse(localStorage.getItem("payments")) || {};

    setParticipants(storedParticipants);
    setSplitPrices(storedSplitPrices); // ตั้งค่า splitPrices จากข้อมูลที่ดึงมา
    setPayments(storedPayments);
  }, []);

  const handlePaymentChange = (participant, amount) => {
    const updatedPayments = {
      ...payments,
      [participant]: parseFloat(amount) || 0,
    };
    setPayments(updatedPayments);
    localStorage.setItem("payments", JSON.stringify(updatedPayments));
  };

  const calculateRemaining = (participant) => {
    return (splitPrices[participant] || 0) - (payments[participant] || 0); // คำนวณยอดเงินที่ยังค้าง
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Status of Debtors
      </Typography>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Participant</TableCell>
              <TableCell>Amount Owed</TableCell>
              <TableCell>Amount Paid</TableCell>
              <TableCell>Remaining</TableCell>
              <TableCell>Update Payment</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {participants.map((participant) => (
              <TableRow key={participant}>
                <TableCell>{participant}</TableCell>
                <TableCell>
                  {splitPrices[participant]
                    ? splitPrices[participant].toFixed(2)
                    : "0.00"} {/* แสดง splitPrice */}
                </TableCell>
                <TableCell>
                  {payments[participant]
                    ? payments[participant].toFixed(2)
                    : "0.00"}
                </TableCell>
                <TableCell>
                  {calculateRemaining(participant).toFixed(2)}
                </TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    label="Payment"
                    variant="outlined"
                    size="small"
                    value={payments[participant] || ""}
                    onChange={(e) =>
                      handlePaymentChange(participant, e.target.value)
                    }
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <ToastContainer />
    </Container>
  );
}
