import React from 'react';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Navbar.css'; // Importing the CSS file for styles

const Navbar = () => {
  const navigate = useNavigate();

  return (
    <AppBar position="static" sx={{ mb: 4, backgroundColor: '#FF6F61' }}>
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
          GlaHarn
        </Typography>
        {['/', '/checkbill', '/summary', '/statusdebtor'].map((path, index) => {
          const pageName = path === '/' ? 'Home' : path.slice(1).charAt(0).toUpperCase() + path.slice(2);
          return (
            <motion.div key={index} whileHover={{ scale: 1.1 }}>
              <Button 
                color="inherit" 
                onClick={() => navigate(path)} 
                sx={{
                  position: 'relative',
                  overflow: 'hidden',
                  '&:hover': {
                    color: '#FF6F61',
                    backgroundColor: 'white',
                    transition: '0.3s',
                  },
                  '&:before': {
                    content: '""',
                    position: 'absolute',
                    height: '100%',
                    width: '100%',
                    backgroundColor: 'white',
                    transform: 'translateY(-100%)',
                    transition: 'transform 0.3s ease',
                    zIndex: -1,
                  },
                  '&:hover:before': {
                    transform: 'translateY(0)',
                  },
                }}
              >
                {pageName}
              </Button>
            </motion.div>
          );
        })}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
