import React, { useState, useEffect } from "react";
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Paper,
  TextField,
} from "@mui/material";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function StatusDebtor() {
  const [participants, setParticipants] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [payments, setPayments] = useState({});

  // Fetching stored data from localStorage when the component mounts
  useEffect(() => {
    const storedParticipants = JSON.parse(localStorage.getItem("participants")) || [];
    const storedTotalAmounts = JSON.parse(localStorage.getItem("totalAmounts")) || {};
    const storedPayments = JSON.parse(localStorage.getItem("payments")) || {};

    setParticipants(storedParticipants);
    setTotalAmounts(storedTotalAmounts);
    setPayments(storedPayments);
  }, []);

  // Handle payment input updates and save to localStorage
  const handlePaymentChange = (participant, amount) => {
    const updatedPayments = {
      ...payments,
      [participant]: parseFloat(amount) || 0,
    };
    setPayments(updatedPayments);
    localStorage.setItem("payments", JSON.stringify(updatedPayments));

    toast.success("Payment updated successfully!");
  };

  // Calculate remaining amount for each participant
  const calculateRemaining = (participant) => {
    return (totalAmounts[participant]?.total || 0) - (payments[participant] || 0);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Status of Debtors
      </Typography>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Participant</TableCell>
              <TableCell>Amount Owed</TableCell>
              <TableCell>Amount Paid</TableCell>
              <TableCell>Remaining</TableCell>
              <TableCell>Update Payment</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {participants.map((participant) => (
              <TableRow key={participant}>
                <TableCell>{participant}</TableCell>
                <TableCell>
                  {totalAmounts[participant]?.foods ? (
                    <>
                      {totalAmounts[participant].foods.map((food, index) => (
                        <Typography key={index} variant="body2">
                          - {food.name}: {food.price.toFixed(2)} B
                        </Typography>
                      ))}
                      <Typography variant="body1">
                        Total: {totalAmounts[participant].total.toFixed(2)} B
                      </Typography>
                    </>
                  ) : (
                    "0.00"
                  )}
                </TableCell>
                <TableCell>
                  {payments[participant] ? payments[participant].toFixed(2) : "0.00"}
                </TableCell>
                <TableCell>
                  {calculateRemaining(participant).toFixed(2)}
                </TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    label="Payment"
                    variant="outlined"
                    size="small"
                    value={payments[participant] || ""}
                    onChange={(e) => handlePaymentChange(participant, e.target.value)}
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <ToastContainer />
    </Container>
  );
}
