import React, { useState, useEffect } from 'react';
import {
  TextField, Button, List, ListItem, Checkbox, Typography, Grid, Paper, Divider, IconButton, Tooltip, Box, Dialog, DialogActions, DialogContent, DialogTitle
} from '@mui/material';
import { motion } from 'framer-motion';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RestaurantMenuIcon from '@mui/icons-material/RestaurantMenu';
import PersonIcon from '@mui/icons-material/Person';

// Helper functions for LocalStorage
const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const [participants, setParticipants] = useState(loadFromLocalStorage('participants'));
  const [participantName, setParticipantName] = useState('');
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage('foodItems'));
  const [foodName, setFoodName] = useState('');
  const [foodPrice, setFoodPrice] = useState('');
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingParticipant, setEditingParticipant] = useState(null); // For editing participants
  const [editingFood, setEditingFood] = useState(null); // For editing food items

  useEffect(() => {
    saveToLocalStorage('participants', participants);
    saveToLocalStorage('foodItems', foodItems);
  }, [participants, foodItems]);

  // Add a new participant
  const handleAddParticipant = () => {
    if (participantName.trim()) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName('');
    }
  };

  // Edit participant name
  const handleEditParticipant = (index, newName) => {
    const updatedParticipants = [...participants];
    updatedParticipants[index] = newName;
    setParticipants(updatedParticipants);
    setEditingParticipant(null); // Close the dialog
  };

  // Reset participants
  const handleResetParticipants = () => {
    setParticipants([]);
    setSelectedParticipants({});
  };

  // Add a new food item
  const handleAddFoodItem = () => {
    if (foodName && foodPrice && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
      setFoodItems([
        ...foodItems,
        {
          name: foodName,
          price: parseFloat(foodPrice),
          participants: Object.keys(selectedParticipants),
          splitPrice,
        },
      ]);
      setFoodName('');
      setFoodPrice('');
      setSelectedParticipants({});
    }
  };

  // Edit food item
  const handleEditFoodItem = (index, newName, newPrice) => {
    const updatedFoodItems = [...foodItems];
    updatedFoodItems[index] = {
      ...updatedFoodItems[index],
      name: newName,
      price: newPrice,
      splitPrice: (newPrice / updatedFoodItems[index].participants.length).toFixed(2),
    };
    setFoodItems(updatedFoodItems);
    setEditingFood(null); // Close the dialog
  };

  // Reset all food items
  const handleResetFoodItems = () => {
    setFoodItems([]);
  };

  // Toggle participant selection for splitting the bill
  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  // Remove individual food item
  const handleRemoveFoodItem = (index) => {
    setFoodItems(foodItems.filter((_, i) => i !== index));
  };

  return (
    <Box p={3}>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.6 }}>
        <Typography variant="h4" align="center" gutterBottom>
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}><PersonIcon /> Add Participants</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <List sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <ListItem key={index} sx={{ justifyContent: 'space-between' }}>
              <Typography>{name}</Typography>
              <Button onClick={() => setEditingParticipant({ index, name })}>Edit</Button>
            </ListItem>
          ))}
        </List>
        <Button variant="outlined" color="secondary" onClick={handleResetParticipants} fullWidth>
          Reset All Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}><RestaurantMenuIcon /> Add Food Items</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
            />
          </Grid>
        </Grid>
        <Typography variant="subtitle1" sx={{ mt: 2 }}>
          Select Participants to Split the Bill:
        </Typography>
        <List>
          {participants.map((name, index) => (
            <ListItem key={index}>
              <Checkbox
                checked={!!selectedParticipants[name]}
                onChange={() => handleSelectParticipant(name)}
              />
              {name}
            </ListItem>
          ))}
        </List>
        <Button variant="contained" color="primary" onClick={handleAddFoodItem} startIcon={<AddIcon />} fullWidth>
          Add Food
        </Button>
      </Paper>

      {/* Food List */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}>Food List</Typography>
        <List>
          {foodItems.map((food, index) => (
            <ListItem
              key={index}
              sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}
            >
              <Box>
                {`${food.name} - $${food.price} split by ${food.participants.join(', ')} ($${food.splitPrice} per person)`}
              </Box>
              <Tooltip title="Remove Item">
                <IconButton color="error" onClick={() => handleRemoveFoodItem(index)}>
                  <DeleteIcon />
                </IconButton>
              </Tooltip>
              <Button onClick={() => setEditingFood({ index, name: food.name, price: food.price })}>Edit</Button>
            </ListItem>
          ))}
        </List>
        <Button variant="outlined" color="secondary" onClick={handleResetFoodItems} fullWidth>
          Reset All Food Items
        </Button>
      </Paper>

      {/* Summary Button */}
      <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
        <Button variant="contained" color="success" fullWidth sx={{ p: 2 }}>
          Make Slip
        </Button>
      </motion.div>

      {/* Edit Participant Modal */}
      {editingParticipant && (
        <Dialog open={true} onClose={() => setEditingParticipant(null)}>
          <DialogTitle>Edit Participant</DialogTitle>
          <DialogContent>
            <TextField
              label="Participant Name"
              value={editingParticipant.name}
              onChange={(e) => setEditingParticipant({ ...editingParticipant, name: e.target.value })}
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setEditingParticipant(null)} color="secondary">Cancel</Button>
            <Button onClick={() => handleEditParticipant(editingParticipant.index, editingParticipant.name)} color="primary">Save</Button>
          </DialogActions>
        </Dialog>
      )}

      {/* Edit Food Item Modal */}
      {editingFood && (
        <Dialog open={true} onClose={() => setEditingFood(null)}>
          <DialogTitle>Edit Food Item</DialogTitle>
          <DialogContent>
            <TextField
                            label="Food Name"
                            value={editingFood.name}
                            onChange={(e) => setEditingFood({ ...editingFood, name: e.target.value })}
                            fullWidth
                          />
                          <TextField
                            label="Price"
                            type="number"
                            value={editingFood.price}
                            onChange={(e) => setEditingFood({ ...editingFood, price: e.target.value })}
                            fullWidth
                            sx={{ mt: 2 }}
                          />
                        </DialogContent>
                        <DialogActions>
                          <Button onClick={() => setEditingFood(null)} color="secondary">Cancel</Button>
                          <Button onClick={() => handleEditFoodItem(editingFood.index, editingFood.name, editingFood.price)} color="primary">Save</Button>
                        </DialogActions>
                      </Dialog>
                    )}
                  </Box>
                );
              };

              export default CheckBill;
