import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page}`);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
      color: '#ffffff',
      overflow: 'hidden',
    },
    button: {
      margin: '20px',
      padding: '15px 30px',
      fontSize: '18px',
      borderRadius: '25px',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      position: 'relative',
      overflow: 'hidden',
      '&:hover': {
        transform: 'scale(1.1)',
        boxShadow: '0 10px 30px rgba(233, 69, 96, 0.5)',
        '&::before': {
          transform: 'scale(2)',
          opacity: 1,
        },
      },
      '&::before': {
        content: '""',
        position: 'absolute',
        top: '50%',
        left: '50%',
        width: '300%',
        height: '300%',
        background: 'radial-gradient(circle, rgba(233, 69, 96, 0.5) 0%, rgba(255, 255, 255, 0) 100%)',
        borderRadius: '50%',
        transition: 'transform 0.3s ease',
        transform: 'translate(-50%, -50%) scale(0)',
        opacity: 0,
      },
    },
    title: {
      marginBottom: '40px',
      fontSize: '3rem',
      textAlign: 'center',
      fontWeight: 'bold',
      textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
    },
  };

  return (
    <Container style={styles.container}>
      <Typography variant="h1" style={styles.title}>
        Welcome to GlaHarn
      </Typography>
      <Grid container justifyContent="center">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Button
            variant="contained"
            color="primary"
            style={styles.button}
            onClick={() => navigateTo('CheckBill')}
          >
            Checkbill
          </Button>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <Button
            variant="contained"
            color="secondary"
            style={styles.button}
            onClick={() => navigateTo('summary')}
          >
            Summary Slip
          </Button>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.4 }}
        >
          <Button
            variant="contained"
            color="secondary"
            style={styles.button}
            onClick={() => navigateTo('statusdebtor')}
          >
            Status Debtor
          </Button>
        </motion.div>
      </Grid>
    </Container>
  );
}
