import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import './Home.css'; // CSS file for styles

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page.toLowerCase()}`); // Ensures the path is lowercase
  };

  return (
    <Container className="home-container" maxWidth="md">
      <Typography variant="h2" className="home-title rainbow-text" align="center">
        Welcome to GlaHarn
      </Typography>
      <Grid container spacing={5} justifyContent="center">
        {['CheckBill', 'Summary', 'StatusDebtor'].map((page, index) => (
          <Grid item key={index} xs={12} sm={4}>
            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ type: 'spring', stiffness: 400, damping: 10 }}
            >
              <Button
                variant="contained"
                className="home-button"
                fullWidth
                onClick={() => navigateTo(page)}
                sx={{
                  bgcolor: '#FF6F61', // Customize the button color
                  '&:hover': {
                    bgcolor: '#FF4B3A', // Hover effect
                  },
                }}
              >
                {page}
              </Button>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}
