    "น้ำบีทรูท",
    "น้ำมะละกอ",
    "ขนมเบื้อง",
    "โซดา",
    "มาร์การิต้า",
    "ค็อกเทล",
    "ไวน์",
    "สก็อตช์",
    "รัม",
    "จิน",
    "มอสโคว์มูด",
    "ช็อต",
    "เหล้าบรั่นดี",
    "สลัดทูน่า",
    "ปีกไก่บาร์บีคิว",
    "สเต๊กเนื้อ",
    "ฟิชแอนด์ชิปส์",
    "อาหารทะเลย่าง",
    "ยำวุ้นเส้น",
    "ข้าวคลุกกะปิ",
    "ซุปกระดูกหมู",
    "ส้มตำไทย",
    "ซี่โครง BBQ",
    "เส้นหมี่",
    "ก๋วยเตี๋ยวเรือ",
    "ข้าวห่อไข่",
    "ไข่เค็ม",
    "หมูกรอบ",
    "หอยทอด",
    "ข้าวมันไก่ทอด",
    "ปีกไก่ย่าง",
    "ข้าวแกงกะหรี่",
    "ขนมโตเกียว",
    "ผัดผักรวม",
    "ส้มตำปู",
    "ปูม้า",
    "ต้มจืด",
    "แกงเผ็ด",
    "ยำหมูยอ",
    "พริกสด",
    "น้ำผักผลไม้",
    "น้ำองุ่น",
    "น้ำมะนาวโซดา",
    "ออส่วน",
    "ข้าวเกรียบ",
    "ขนมชั้น",
    "ข้าวซอยเนื้อ",
    "น้ำมะม่วง",
    "ปีกไก่ทอดน้ำปลา",
    "แกงไก่",
    "กุ้งทอดกระเทียม",
    "ขนมจีนน้ำยาปู",
    "ข้าวต้ม",
    "ขนมปังหน้าไก่",
    "น้ำพั้นช์",
    "ห่อหมก",
    "น้ำจิ้มซีฟู้ด",
    "เกี๊ยวซ่า",
    "ผัดกระเพราเห็ด",
    "ปลาหมึกย่าง",
    "กะเพราไข่ดาว",
    "ข้าวผัดต้มยำ",
    "ขนมเกสร",
    "ซุปเห็ด",
    "หมูย่าง",
    "เส้นหมี่อบ",
    "หมี่กรอบ",
    "ข้าวไข่ข้น",
    "น้ำลูกเดือย",
    "น้ำส้ม",
    "น้ำมะพร้าวปั่น",
    "ชามะนาว",
    "ขนมช็อกโกแลต",
    "พิซซ่า",
    "ข้าวกะเพรา",
    "หมูสเต๊ะ",
    "ข้าวมันส้มตำ",
  ];

  const handleAddFoodItem = () => {
    const trimmedFoodName = foodName.trim(); // Trim ชื่ออาหาร

    const newFoodItem = {
        id: uuidv4(),
        name: trimmedFoodName || foodName, // ใช้ trimmedFoodName หรือ foodName
        price: parseFloat(foodPrice) || 0,
        participants: Object.keys(selectedParticipants).filter(
            (name) => selectedParticipants[name]
        ),
        splitPrice:
            Object.keys(selectedParticipants).length > 0
                ? (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2)
                : 0,
    };

    if (trimmedFoodName && foodPrice) { // ตรวจสอบชื่อและราคา
        setFoodItems([...foodItems, newFoodItem]);
        resetFoodInputs();
        enqueueSnackbar("Food item added!", { variant: "success" });

        // เก็บข้อมูลใน localStorage
        localStorage.setItem("foodItems", JSON.stringify([...foodItems, newFoodItem]));
    } else {
        enqueueSnackbar("Please enter a valid food name and price.", {
            variant: "error",
        });
    }
};


  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(
      participants.reduce((acc, participant) => {
        acc[participant] = food.participants.includes(participant);
        return acc;
      }, {})
    );
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (
            parseFloat(foodPrice) / Object.keys(selectedParticipants).length
          ).toFixed(2);
          return {
            ...food,
            name: foodName,
            price: parseFloat(foodPrice),
            participants: Object.keys(selectedParticipants),
            splitPrice,
          };
        }
        return food;
      });

      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box
      p={3}
      sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}
    >
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <PersonIcon /> Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{
                "& .MuiOutlinedInput-root": {
                  "& fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.12)",
                  },
                  "&:hover fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.5)",
                  },
                  "&.Mui-focused fieldset": {
                    borderColor: "#FF6F61",
                  },
                },
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
              sx={{
                transition: "0.3s",
                "&:hover": {
                  backgroundColor: "#FF4B3A",
                },
              }}
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              sx={{
                m: 0.5,
                backgroundColor: "#FF6F61",
                color: "white",
                "&:hover": {
                  backgroundColor: "#FF4B3A",
                },
              }}
              onDelete={() => handleRemoveParticipant(index)}
            />
          ))}
        </Box>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleResetParticipants}
          fullWidth
        >
          Reset All Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <Autocomplete
              freeSolo
              options={exampleFoodItems}
              value={foodName}
              onChange={(event, newValue) => setFoodName(newValue)}
              renderInput={(params) => (
                <TextField {...params} label="Food Name" variant="outlined" />
              )}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price (฿)"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              inputProps={{ min: 0 }} // Prevent negative prices
              variant="outlined"
            />
          </Grid>
        </Grid>
        <Typography variant="subtitle1" sx={{ mt: 2 }}>
          Select Participants to Split the Bill:
        </Typography>
        <Box sx={{ display: "flex", flexWrap: "wrap", mt: 1 }}>
          {participants.map((name) => (
            <Chip
              key={name}
              label={name}
              onClick={() => handleSelectParticipant(name)}
              sx={{
                m: 0.5,
                backgroundColor: selectedParticipants[name]
                  ? "#FF6F61"
                  : "rgba(0, 0, 0, 0.12)",
                color: selectedParticipants[name] ? "white" : "black",
                "&:hover": {
                  backgroundColor: selectedParticipants[name]
                    ? "#FF4B3A"
                    : "rgba(0, 0, 0, 0.2)",
                },
              }}
            />
          ))}
        </Box>
        {editingFoodId ? (
          <Button
            variant="contained"
            color="primary"
            onClick={handleUpdateFoodItem}
            fullWidth
            sx={{ mt: 2 }}
          >
            Update Food Item
          </Button>
        ) : (
          <Button
            variant="contained"
            color="primary"
            onClick={handleAddFoodItem}
            fullWidth
            sx={{ mt: 2 }}
          >
            Add Food Item
          </Button>
        )}
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleResetFoodItems}
          fullWidth
          sx={{ mt: 2 }}
        >
          Reset All Food Items
        </Button>
      </Paper>

      {/* List of Food Items */}
      <List sx={{ mb: 3 }}>
        {foodItems.length > 0 ? (
          foodItems.map((food) => (
            <ListItem
              key={food.id}
              sx={{
                border: "1px solid rgba(0, 0, 0, 0.12)",
                borderRadius: 2,
                mb: 2,
                p: 2,
              }}
            >
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={4}>
                  <Typography variant="body1" sx={{ fontWeight: "bold" }}>
                    {food.name}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    {food.participants.join(", ")}
                  </Typography>
                </Grid>
                <Grid item xs={3}>
                  <Typography variant="body1">
                    {food.price.toFixed(2)} ฿
                  </Typography>
                </Grid>
                <Grid item xs={3}>
                  <Typography variant="body1" color="primary">
                    {food.splitPrice} ฿ each
                  </Typography>
                </Grid>
                <Grid item xs={2}>
                  <IconButton
                    color="secondary"
                    onClick={() => handleEditFoodItem(food)}
                  >
                    <EditIcon />
                  </IconButton>
                  <IconButton
                    color="error"
                    onClick={() => handleRemoveFoodItem(food.id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Grid>
              </Grid>
            </ListItem>
          ))
        ) : (
          <Typography variant="body2" color="textSecondary">
            No food items added yet.
          </Typography>
        )}
      </List>

      {/* Total Summary */}
      <Box sx={{ mb: 3 }}>
        <Typography variant="h5" sx={{ mb: 2 }} color="secondary">
          Total Summary
        </Typography>
        {Object.keys(totalSummary).map((participant) => (
          <Typography key={participant} variant="body1">
            {participant}: {totalSummary[participant].toFixed(2)} ฿
          </Typography>
        ))}
      </Box>

      {/* Reset and Summary Buttons */}
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <Button
            variant="outlined"
            color="error"
            onClick={handleResetAllData}
            fullWidth
            sx={{ fontSize: "0.8rem", height: 50 }}
          >
            Reset All Data
          </Button>
        </Grid>
        <Grid item xs={6}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            sx={{ fontSize: "1rem", height: 50 }}
            onClick={() => (window.location.href = "/summary")} // Assume summary page route
          >
            Make Slip Summary
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default CheckBill;
