import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { motion } from "framer-motion";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs
import Autocomplete from "@mui/material/Autocomplete";
import { useHistory } from "react-router-dom"; // Import useHistory

const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const history = useHistory(); // Initialize useHistory
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(loadFromLocalStorage("participants"));
  const [participantName, setParticipantName] = useState("");
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingFoodId, setEditingFoodId] = useState(null);

  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  useEffect(() => {
    const storedFoodItems = JSON.parse(localStorage.getItem("foodItems")) || [];
    setFoodItems(storedFoodItems);
  }, []);

  const handleAddParticipant = () => {
    if (participantName.trim() && !participants.includes(participantName.trim())) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", { variant: "error" });
    }
  };

  const handleRemoveParticipant = (index) => {
    setParticipants(participants.filter((_, i) => i !== index));
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleResetFoodItems = () => {
    setFoodItems([]);
    enqueueSnackbar("All food items reset!", { variant: "info" });
  };

  const handleResetAllData = () => {
    handleResetParticipants();
    handleResetFoodItems();
    enqueueSnackbar("All data has been reset!", { variant: "info" });
  };

  const exampleFoodItems = [
    // (Your existing food items)
  ];

  const handleAddFoodItem = () => {
    const trimmedFoodName = foodName.trim(); // Trim food name

    const newFoodItem = {
      id: uuidv4(),
      name: trimmedFoodName,
      price: parseFloat(foodPrice) || 0,
      participants: Object.keys(selectedParticipants).filter((name) => selectedParticipants[name]),
      splitPrice: Object.keys(selectedParticipants).length > 0
        ? (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2)
        : 0,
    };

    if (trimmedFoodName && foodPrice) {
      setFoodItems((prevItems) => [...prevItems, newFoodItem]);
      resetFoodInputs();
      enqueueSnackbar("Food item added!", { variant: "success" });

      // Save to local storage
      localStorage.setItem("foodItems", JSON.stringify([...foodItems, newFoodItem]));

      // Navigate to the food list after adding the food item
      history.push("/food-list"); // Change this to your food list route
    } else {
      enqueueSnackbar("Please enter a valid food name and price.", { variant: "error" });
    }
  };

  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(
      participants.reduce((acc, participant) => {
        acc[participant] = food.participants.includes(participant);
        return acc;
      }, {})
    );
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
          return {
            ...food,
            name: foodName,
            price: parseFloat(foodPrice),
            participants: Object.keys(selectedParticipants),
            splitPrice,
          };
        }
        return food;
      });

      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box p={3} sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.6 }}>
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      {/* (Your existing participants section) */}

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <Autocomplete
              freeSolo
              options={exampleFoodItems}
              value={foodName}
              onChange={(event, newValue) => setFoodName(newValue)}
              renderInput={(params) => (
                <TextField {...params} label="Food Name" variant="outlined" />
              )}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price (à¸¿)"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              inputProps={{ min: 0 }}
              variant="outlined"
            />
          </Grid>
        </Grid>
        <Typography variant="subtitle1" sx={{ mt: 2 }}>
          Select Participants to Split the Bill:
        </Typography>
        <Box sx={{ display: "flex", flexWrap: "wrap", mt: 1 }}>
          {participants.map((name) => (
            <Chip
              key={name}
              label={name}
              onClick={() => handleSelectParticipant(name)}
              sx={{
                m: 0.5,
                backgroundColor: selectedParticipants[name] ? "#FF6F61" : "rgba(0, 0, 0, 0.12)",
                color: selectedParticipants[name] ? "white" : "black",
                "&:hover": {
                  backgroundColor: selectedParticipants[name] ? "#FF4B3A" : "rgba(0, 0, 0, 0.2)",
                },
              }}
            />
          ))}
        </Box>
        {editingFoodId ? (
          <Button variant="contained" color="primary" onClick={handleUpdateFoodItem} fullWidth sx={{ mt: 2 }}>
            Update Food Item
          </Button>
        ) : (
          <Button variant="contained" color="primary" onClick={handleAddFoodItem} fullWidth sx={{ mt: 2 }}>
            Add Food Item
          </Button>
        )}
      </Paper>

      {/* Food Items List Section */}
      {/* (Your existing food items list) */}

      {/* Summary Section */}
      {/* (Your existing summary section) */}

      {/* Reset All Data Button */}
      <Button variant="outlined" color="secondary" onClick={handleResetAllData} fullWidth>
        Reset All Data
      </Button>
    </Box>
  );
};

export default CheckBill;
