import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page}`);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
      color: '#ffffff',
      overflow: 'hidden',
    },
    button: {
      margin: '20px',
      padding: '15px 30px',
      fontSize: '20px',
      borderRadius: '30px',
      position: 'relative',
      overflow: 'hidden',
      fontWeight: 'bold',
      color: '#ffffff',
      textTransform: 'uppercase',
      backgroundColor: '#FF7F00',
      boxShadow: '0 5px 15px rgba(0,0,0,0.2)',
      transition: 'transform 0.3s ease, background-color 0.3s ease',
      '&:hover': {
        transform: 'scale(1.1)',
        backgroundColor: '#FF0000',
        boxShadow: '0 15px 25px rgba(233, 69, 96, 0.5)',
      },
      '&:active': {
        transform: 'scale(0.95)',
        boxShadow: '0 5px 15px rgba(0,0,0,0.2)',
      },
    },
    title: {
      marginBottom: '40px',
      fontSize: '3rem',
      textAlign: 'center',
      fontWeight: 'bold',
      textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
      animation: 'rainbow 5s infinite linear, move 1.5s infinite alternate ease-in-out',
      background: 'linear-gradient(270deg, #FF0000, #FF7F00, #FFFF00, #00FF00, #00FFFF, #0000FF, #4B0082, #9400D3)',
      backgroundSize: '400% 400%',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
    },
    buttonContainer: {
      position: 'relative',
      '&:hover::after': {
        content: '""',
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',
        backgroundColor: 'rgba(255, 255, 255, 0.2)',
        borderRadius: '30px',
        transition: 'opacity 0.3s ease',
        opacity: '1',
      },
      '&::after': {
        content: '""',
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',
        backgroundColor: 'rgba(255, 255, 255, 0.2)',
        borderRadius: '30px',
        transition: 'opacity 0.3s ease',
        opacity: '0',
      },
    },
  };

  // Create a style element to inject keyframes dynamically
  const styleElement = document.createElement('style');
  styleElement.innerHTML = `
    @keyframes rainbow {
      0% { background-position: 0% 50%; }
      100% { background-position: 100% 50%; }
    }
    @keyframes move {
      0% { transform: translateX(-10px); }
      100% { transform: translateX(10px); }
    }
    @keyframes sparkle {
      0% { opacity: 0; }
      50% { opacity: 1; }
      100% { opacity: 0; }
    }
  `;
  document.head.appendChild(styleElement);

  return (
    <Container style={styles.container}>
      <Typography variant="h1" style={styles.title}>
        Welcome to GlaHarn
      </Typography>
      <Grid container justifyContent="center">
        {['CheckBill', 'summary', 'statusdebtor'].map((page, index) => (
          <motion.div
            key={index}
            whileHover={{ scale: 1.05, rotate: 2 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: 'spring', stiffness: 400, damping: 10 }}
            style={styles.buttonContainer}
          >
            <Button
              variant="contained"
              style={styles.button}
              onClick={() => navigateTo(page)}
            >
              {page.charAt(0).toUpperCase() + page.slice(1).replace(/([A-Z])/g, ' $1')}
            </Button>
          </motion.div>
        ))}
      </Grid>
      <motion.div
        animate={{ rotate: 360 }}
        transition={{ duration: 20, loop: Infinity, ease: "linear" }}
        style={{
          position: 'absolute',
          top: '20%',
          left: '20%',
          width: '150px',
          height: '150px',
          borderRadius: '50%',
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          backdropFilter: 'blur(10px)',
          boxShadow: '0 0 20px rgba(255, 255, 255, 0.5)',
          zIndex: 0,
        }}
      />
    </Container>
  );
}
