import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, Grid, Paper, Input } from '@mui/material';
import html2canvas from 'html2canvas';

const Summary = () => {
  const [participants, setParticipants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [qrCodeFile, setQrCodeFile] = useState(null);

  useEffect(() => {
    const savedParticipants = JSON.parse(localStorage.getItem('participants'));
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems'));

    if (savedParticipants) setParticipants(savedParticipants);
    if (savedFoodItems) {
      setFoodItems(savedFoodItems);
      calculateTotals(savedParticipants, savedFoodItems);
    }
  }, []);

  const calculateTotals = (participants, items) => {
    const totals = {};

    participants.forEach(participant => {
      totals[participant] = { total: 0, foods: [] };
    });

    items.forEach(item => {
      const splitPrice = item.price / item.participants.length;
      item.participants.forEach(participant => {
        if (totals[participant]) {
          totals[participant].total += splitPrice;
          totals[participant].foods.push({ name: item.name, price: splitPrice });
        }
      });
    });

    setTotalAmounts(totals);
  };

  const handleQrCodeChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setQrCodeFile(URL.createObjectURL(file));
    }
  };

  const saveSlipAsImage = () => {
    const slipElement = document.getElementById('summary-slip');
    html2canvas(slipElement).then(canvas => {
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'summary-slip.png';
      link.click();
    });
  };

  if (Object.keys(totalAmounts).length === 0) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h5">Please fill out the CheckBill information first.</Typography>
      </Box>
    );
  }

  return (
    <Box id="summary-slip" sx={{ p: 3, background: 'linear-gradient(to bottom right, #ffeb3b, #f44336)', borderRadius: '16px', boxShadow: 10, maxWidth: '600px', mx: 'auto', mt: 4 }}>
      <Typography variant="h3" align="center" sx={{ mb: 3, fontWeight: 'bold', color: '#fff', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)' }}>
        ðŸ§¾ Receipt Summary
      </Typography>
      <Grid container spacing={2}>
        {participants.map((participant, index) => (
          <Grid item xs={12} key={index}>
            <Paper elevation={8} sx={{ padding: 3, marginBottom: 2, borderRadius: 8, backgroundColor: '#fff', border: '2px solid #ffeb3b', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
              <Typography variant="h5" sx={{ color: '#f44336', fontWeight: 'bold' }}>{participant}</Typography>
              <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#4CAF50' }}>
                Total: {totalAmounts[participant]?.total.toFixed(2) || 0} B
              </Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold', marginBottom: 1 }}>Food Items:</Typography>
              {totalAmounts[participant]?.foods.map((food, foodIndex) => (
                <Typography variant="body2" key={foodIndex} sx={{ pl: 2, color: '#555' }}>
                  - {food.name}: {food.price.toFixed(2)} B
                </Typography>
              ))}
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="h6" sx={{ marginBottom: 2, fontWeight: 'bold' }}>Attach QR Code:</Typography>
        <Input type="file" accept="image/*" onChange={handleQrCodeChange} />
        {qrCodeFile && <img src={qrCodeFile} alt="QR Code" style={{ maxWidth: '100%', marginTop: '10px', border: '2px solid #f44336', borderRadius: '8px' }} />}
        <Button variant="contained" color="primary" onClick={saveSlipAsImage} sx={{ mt: 2 }}>
          Save as Image
        </Button>
      </Box>
    </Box>
  );
};

export default Summary;
