import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { motion } from "framer-motion";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs

const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(
    loadFromLocalStorage("participants")
  );
  const [participantName, setParticipantName] = useState("");
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingFoodId, setEditingFoodId] = useState(null);
  const [filterText, setFilterText] = useState(""); // New state for filter text

  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  const handleAddParticipant = () => {
    if (
      participantName.trim() &&
      !participants.includes(participantName.trim())
    ) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", {
        variant: "error",
      });
    }
  };

  const handleRemoveParticipant = (index) => {
    setParticipants(participants.filter((_, i) => i !== index));
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleResetFoodItems = () => {
    setFoodItems([]);
    enqueueSnackbar("All food items reset!", { variant: "info" });
  };

  const handleResetAllData = () => {
    handleResetParticipants();
    handleResetFoodItems();
    enqueueSnackbar("All data has been reset!", { variant: "info" });
  };

  const handleAddFoodItem = () => {
    if (
      foodName &&
      foodPrice > 0 &&
      Object.keys(selectedParticipants).length > 0
    ) {
      const splitPrice = (
        parseFloat(foodPrice) / Object.keys(selectedParticipants).length
      ).toFixed(2);

      const newFoodItem = {
        id: uuidv4(),
        name: foodName,
        price: parseFloat(foodPrice),
        participants: Object.keys(selectedParticipants),
        splitPrice,
      };

      setFoodItems([...foodItems, newFoodItem]);
      resetFoodInputs();
      enqueueSnackbar("Food item added!", { variant: "success" });
    } else {
      enqueueSnackbar("Please fill all fields correctly.", {
        variant: "error",
      });
    }
  };

  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(
      participants.reduce((acc, participant) => {
        acc[participant] = food.participants.includes(participant);
        return acc;
      }, {})
    );
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (
            parseFloat(foodPrice) / Object.keys(selectedParticipants).length
          ).toFixed(2);
          return {
            ...food,
            name: foodName,
            price: parseFloat(foodPrice),
            participants: Object.keys(selectedParticipants),
            splitPrice,
          };
        }
        return food;
      });

      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  // Filter food items based on filterText
  const filteredFoodItems = foodItems.filter((food) =>
    food.name.toLowerCase().includes(filterText.toLowerCase())
  );

  return (
    <Box
      p={3}
      sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}
    >
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <PersonIcon /> Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{
                "& .MuiOutlinedInput-root": {
                  "& fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.12)",
                  },
                  "&:hover fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.5)",
                  },
                  "&.Mui-focused fieldset": {
                    borderColor: "#FF6F61",
                  },
                },
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
              sx={{
                transition: "0.3s",
                "&:hover": {
                  backgroundColor: "#FF4B3A",
                },
              }}
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              sx={{
                m: 0.5,
                backgroundColor: "#FF6F61",
                color: "white",
                "&:hover": {
                  backgroundColor: "#FF4B3A",
                },
              }}
              onDelete={() => handleRemoveParticipant(index)}
            />
          ))}
        </Box>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleResetParticipants}
          fullWidth
        >
          Reset All Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price (฿)"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name) => (
            <Chip
              key={name}
              label={name}
              color={selectedParticipants[name] ? "primary" : "default"}
              onClick={() => handleSelectParticipant(name)}
              sx={{
                m: 0.5,
                cursor: "pointer",
              }}
            />
          ))}
        </Box>
        <Button
          variant="contained"
          color="primary"
          onClick={editingFoodId ? handleUpdateFoodItem : handleAddFoodItem}
          sx={{ mt: 2 }}
          fullWidth
        >
          {editingFoodId ? "Update Food Item" : "Add Food Item"}
        </Button>
        <TextField
          label="Filter Food Items"
          value={filterText}
          onChange={(e) => setFilterText(e.target.value)}
          fullWidth
          variant="outlined"
          sx={{ mt: 2 }}
        />
      </Paper>

      {/* Food Items List */}
      <Paper elevation={3} sx={{ p: 2, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          Food Items
        </Typography>
        <List>
          {filteredFoodItems.map((food) => (
            <ListItem key={food.id}>
              <ListItemText
                primary={food.name}
                secondary={`Price: ฿${food.price} | Split: ฿${food.splitPrice} per person`}
              />
              <IconButton onClick={() => handleEditFoodItem(food)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => handleRemoveFoodItem(food.id)}>
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleResetFoodItems}
          fullWidth
          sx={{ mt: 2 }}
        >
          Reset All Food Items
        </Button>
        <Button
          variant="outlined"
          color="error"
          onClick={handleResetAllData}
          fullWidth
          sx={{ mt: 2 }}
        >
          Reset All Data
        </Button>
      </Paper>

      {/* Summary Section */}
      <Paper elevation={3} sx={{ p: 2, mt: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="secondary">
          Payment Summary
        </Typography>
        {Object.entries(totalSummary).map(([participant, total]) => (
          <Typography key={participant}>
            {participant}: ฿{total.toFixed(2)}
          </Typography>
        ))}
      </Paper>
    </Box>
  );
};

export default CheckBill;
