import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, Grid, Input } from '@mui/material';
import html2canvas from 'html2canvas';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

const Summary = () => {
  const [participants, setParticipants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [qrCodeFile, setQrCodeFile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const savedParticipants = JSON.parse(localStorage.getItem('participants'));
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems'));

    if (savedParticipants) setParticipants(savedParticipants);
    if (savedFoodItems) {
      setFoodItems(savedFoodItems);
      calculateTotals(savedParticipants, savedFoodItems);
    }
  }, []);

  const calculateTotals = (participants, items) => {
    const totals = {};
    participants.forEach(participant => {
      totals[participant] = { total: 0, foods: [] };
    });
    items.forEach(item => {
      const splitPrice = item.price / item.participants.length;
      item.participants.forEach(participant => {
        if (totals[participant]) {
          totals[participant].total += splitPrice;
          totals[participant].foods.push({ name: item.name, price: splitPrice });
        }
      });
    });
    setTotalAmounts(totals);
  };

  const handleQrCodeChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setQrCodeFile(URL.createObjectURL(file));
    }
  };

  const saveSlipAsImage = () => {
    const slipElement = document.getElementById('summary-slip');
    html2canvas(slipElement).then(canvas => {
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'summary-slip.png';
      link.click();
    });
  };

  const handleBackClick = () => {
    navigate('/checkbill');
  };

  if (Object.keys(totalAmounts).length === 0) {
    return (
      <Box sx={{ p: 3, textAlign: 'center', background: 'linear-gradient(45deg, #ff6e40, #ff8e53)' }}>
        <Typography variant="h5" sx={{ color: '#ffffff' }}>
          Please fill out the CheckBill information first.
        </Typography>
        <Button 
          variant="contained" 
          onClick={handleBackClick} 
          sx={{ mt: 2, backgroundColor: '#3f51b5', '&:hover': { backgroundColor: '#303f9f' } }}
        >
          Go to Check Bill
        </Button>
      </Box>
    );
  }

  return (
    <Box id="summary-slip" sx={{
      p: 3,
      background: 'linear-gradient(to right, #1a1a2e, #16213e, #0f3460)', 
      borderRadius: '16px', 
      boxShadow: 10, 
      maxWidth: '800px', 
      mx: 'auto', 
      mt: 4,
      color: '#ffffff',  
      position: 'relative',
      overflow: 'hidden',
    }}>
      <Typography variant="h3" align="center" sx={{
        mb: 3, 
        fontWeight: 'bold', 
        textShadow: '0 0 10px rgba(233, 69, 96, 0.8), 0 0 20px rgba(233, 69, 96, 0.6)', 
      }}>
        🚀 Receipt Summary
      </Typography>
      <Grid container spacing={2}>
        {participants.map((participant, index) => (
          <Grid item xs={12} md={6} key={index}>
            <motion.div 
              initial={{ scale: 0.8 }} 
              animate={{ scale: 1 }} 
              transition={{ duration: 0.3 }} 
              style={{
                perspective: '1000px',
              }}
            >
              <Box 
                sx={{
                  padding: 3, 
                  borderRadius: 2, 
                  backgroundColor: 'rgba(255, 255, 255, 0.1)', 
                  border: '2px solid #e94560', 
                  backdropFilter: 'blur(10px)', 
                  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)', 
                  transition: 'transform 0.2s', 
                  '&:hover': { transform: 'rotateY(10deg)' }
                }}
              >
                <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#ffffff' }}>
                  {participant}
                </Typography>
                <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#ffffff' }}>
                  Total: {totalAmounts[participant]?.total.toFixed(2) || 0} B
                </Typography>
                <Typography variant="body1" sx={{ fontWeight: 'bold', marginBottom: 1, color: '#ffffff' }}>
                  Food Items:
                </Typography>
                {totalAmounts[participant]?.foods.map((food, foodIndex) => (
                  <Typography variant="body2" key={foodIndex} sx={{ pl: 2, color: '#ffffff' }}>
                    - {food.name}: {food.price.toFixed(2)} B
                  </Typography>
                ))}
              </Box>
            </motion.div>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="h6" sx={{ marginBottom: 2, fontWeight: 'bold', color: '#ffffff' }}>
          Attach QR Code:
        </Typography>
        <Input type="file" accept="image/*" onChange={handleQrCodeChange} />
        {qrCodeFile && (
          <Box sx={{
            marginTop: '10px', 
            border: '2px solid #e94560', 
            borderRadius: '8px', 
            padding: '10px',
            position: 'relative',
            overflow: 'hidden',
            display: 'inline-block'
          }}>
            <img 
              src={qrCodeFile} 
              alt="QR Code" 
              style={{ maxWidth: '100%', borderRadius: '8px', transform: 'scale(1.1)', transition: 'transform 0.3s' }} 
              onMouseOver={(e) => (e.currentTarget.style.transform = 'scale(1.1)')} 
              onMouseOut={(e) => (e.currentTarget.style.transform = 'scale(1)')} 
            />
          </Box>
        )}
        <Button 
          variant="contained" 
          color="primary" 
          onClick={saveSlipAsImage} 
          sx={{ mt: 2, backgroundColor: '#e94560', '&:hover': { backgroundColor: '#d83b5c' } }}
        >
          Save as Image
        </Button>
        <Button 
          variant="contained" 
          onClick={handleBackClick} 
          sx={{ mt: 2, ml: 2, backgroundColor: '#3f51b5', '&:hover': { backgroundColor: '#303f9f' } }}
        >
          Back to Check Bill
        </Button>
      </Box>
    </Box>
  );
};

export default Summary;
