import React, { useState, useEffect } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Grid,
  Checkbox,
  FormControlLabel,
  Paper,
} from "@mui/material";
import { ToastContainer, toast } from "react-toastify";
import { motion } from "framer-motion";
import "react-toastify/dist/ReactToastify.css";

const CheckBill = () => {
    return (
        <Container style={styles.container}>
          <Typography variant="h4" style={styles.title}>
            CheckBills
          </Typography>

          <motion.div
            style={styles.section}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Typography variant="h6">Add Participants</Typography>
            <Grid container spacing={1} alignItems="center">
              <Grid item xs={8}>
                <TextField
                  label="Participant Name"
                  value={newParticipant}
                  onChange={(e) => setNewParticipant(e.target.value)}
                  fullWidth
                  variant="outlined"
                  style={{ marginBottom: "10px" }}
                />
              </Grid>
              <Grid item xs={4}>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={addParticipant}
                  style={styles.button}
                >
                  Add
                </Button>
              </Grid>
            </Grid>
            <div style={{ display: "flex", flexWrap: "wrap", marginTop: "10px" }}>
              {participants.map((participant, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, scale: 0.5 }}
                  animate={{ opacity: 1, scale: 1 }}
                  exit={{ opacity: 0, scale: 0 }}
                  transition={{ duration: 0.3 }}
                >
                  <Chip
                    label={participant}
                    onDelete={() =>
                      setParticipants((prev) =>
                        prev.filter((p) => p !== participant)
                      )
                    }
                    style={{
                      margin: "5px",
                      backgroundColor: "#FFCCFF",
                      color: "#000",
                    }}
                  />
                </motion.div>
              ))}
            </div>
            <Button
              variant="outlined"
              color="secondary"
              onClick={resetParticipants}
              style={styles.button}
            >
              Reset All Participants
            </Button>
          </motion.div>

          <motion.div
            style={styles.section}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Typography variant="h6">Add Food Items</Typography>
            <TextField
              label="Food Name"
              value={currentFood.name}
              onChange={(e) => handleFoodChange("name", e.target.value)}
              fullWidth
              variant="outlined"
              style={{ marginBottom: "10px" }}
            />
            <TextField
              label="Price"
              type="number"
              value={currentFood.price !== 0 ? currentFood.price : ""}
              onChange={(e) => handleFoodChange("price", e.target.value)}
              fullWidth
              variant="outlined"
              style={{ marginBottom: "10px" }}
            />
            <Typography variant="subtitle1">Share with:</Typography>
            {participants.map((participant, index) => (
              <FormControlLabel
                key={index}
                control={
                  <Checkbox
                    checked={currentFood.sharedWith[index] || false}
                    onChange={() => {
                      const newSharedWith = [...currentFood.sharedWith];
                      newSharedWith[index] = !newSharedWith[index];
                      setCurrentFood((prev) => ({
                        ...prev,
                        sharedWith: newSharedWith,
                      }));
                    }}
                  />
                }
                label={participant}
              />
            ))}

            <Button
              variant="contained"
              color="primary"
              onClick={addFoodItem}
              style={styles.button}
            >
              {editingFoodIndex !== null ? "Save Changes" : "Add Food"}
            </Button>

            <div style={{ marginTop: "20px" }}>
              {foodItems.map((foodItem, index) => (
                <Paper key={index} style={styles.foodItem}>
                  <Typography variant="subtitle1">
                    {foodItem.name} - ${foodItem.price.toFixed(2)}
                  </Typography>
                  <Typography variant="body2">
                    Shared with:{" "}
                    {foodItem.sharedWith
                      .map((shared, idx) => (shared ? participants[idx] : null))
                      .filter(Boolean)
                      .join(", ")}
                  </Typography>
                  <Button
                    onClick={() => editFoodItem(index)}
                    color="primary"
                    size="small"
                  >
                    Edit
                  </Button>
                  <Button
                    onClick={() => deleteFoodItem(index)}
                    color="secondary"
                    size="small"
                  >
                    Delete
                  </Button>
                </Paper>
              ))}
            </div>
            <Button
              variant="outlined"
              color="secondary"
              onClick={resetFoodItems}
              style={styles.button}
            >
              Reset All Food Items
            </Button>
          </motion.div>

          <motion.div
            style={styles.section}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <Typography variant="h6">Summary</Typography>
            <TableContainer>
              <Table style={styles.summaryTable}>
                <TableHead>
                  <TableRow>
                    <TableCell>Participant</TableCell>
                    <TableCell align="right">Total Owes ($)</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {participants.map((participant, index) => (
                    <TableRow key={index}>
                      <TableCell>{participant}</TableCell>
                      <TableCell align="right">
                        {totalExpenses[index].toFixed(2)}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </motion.div>

          <ToastContainer />
        </Container>
      );

};

export default CheckBill;
