import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page}`);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
      color: '#ffffff',
      overflow: 'hidden',
    },
    button: {
      margin: '20px',
      padding: '15px 30px',
      fontSize: '20px',
      borderRadius: '30px',
      position: 'relative',
      overflow: 'hidden',
      fontWeight: 'bold',
      color: '#ffffff',
      textTransform: 'uppercase',
      transition: 'transform 0.3s ease',
      boxShadow: '0 5px 15px rgba(0,0,0,0.2)',
      '&:hover': {
        transform: 'translateY(-5px)',
        boxShadow: '0 10px 30px rgba(233, 69, 96, 0.5)',
        background: 'linear-gradient(45deg, #FF7F00, #FF0000)',
        animation: 'pulse 0.5s infinite alternate',
      },
      '&:focus': {
        outline: 'none',
        border: '2px solid #FF7F00',
      },
    },
    title: {
      marginBottom: '40px',
      fontSize: '3rem',
      textAlign: 'center',
      fontWeight: 'bold',
      textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)',
      animation: 'rainbow 5s infinite linear, move 1.5s infinite alternate ease-in-out',
      background: 'linear-gradient(270deg, #FF0000, #FF7F00, #FFFF00, #00FF00, #00FFFF, #0000FF, #4B0082, #9400D3)',
      backgroundSize: '400% 400%',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
    },
    '@keyframes rainbow': {
      '0%': { backgroundPosition: '0% 50%' },
      '100%': { backgroundPosition: '100% 50%' },
    },
    '@keyframes move': {
      '0%': { transform: 'translateX(-10px)' },
      '100%': { transform: 'translateX(10px)' },
    },
    '@keyframes pulse': {
      '0%': { transform: 'scale(1)' },
      '100%': { transform: 'scale(1.05)' },
    },
  };

  // Create a style element to inject keyframes dynamically
  const styleElement = document.createElement('style');
  styleElement.innerHTML = `
    @keyframes rainbow {
      0% { background-position: 0% 50%; }
      100% { background-position: 100% 50%; }
    }
    @keyframes move {
      0% { transform: translateX(-10px); }
      100% { transform: translateX(10px); }
    }
    @keyframes pulse {
      0% { transform: scale(1); }
      100% { transform: scale(1.05); }
    }
  `;
  document.head.appendChild(styleElement);

  return (
    <Container style={styles.container}>
      <Typography variant="h1" style={styles.title}>
        Welcome to GlaHarn
      </Typography>
      <Grid container justifyContent="center">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <Button
            variant="contained"
            style={styles.button}
            onClick={() => navigateTo('CheckBill')}
          >
            Check Bill
          </Button>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <Button
            variant="contained"
            style={styles.button}
            onClick={() => navigateTo('summary')}
          >
            Summary Slip
          </Button>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.4 }}
        >
          <Button
            variant="contained"
            style={styles.button}
            onClick={() => navigateTo('statusdebtor')}
          >
            Status Debtor
          </Button>
        </motion.div>
      </Grid>
    </Container>
  );
}
