import React from 'react';
import { Box, Paper, Typography, Button, Grid, Divider } from '@mui/material';
import { QRCode } from 'react-qrcode-logo';
import * as htmlToImage from 'html-to-image';
import jsPDF from 'jspdf';
import { useLocation } from 'react-router-dom';

const Summary = () => {
  const location = useLocation();
  const { totalSummary = {}, foodItems = [] } = location.state || {}; // Default values

  const qrCodeValue = "https://example.com/payment"; // Update with actual payment link

  const saveAsImage = () => {
    const summaryNode = document.getElementById('summary-slip');
    htmlToImage.toPng(summaryNode)
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = 'summary-slip.png';
        link.href = dataUrl;
        link.click();
      });
  };

  const saveAsPdf = () => {
    const summaryNode = document.getElementById('summary-slip');
    htmlToImage.toPng(summaryNode)
      .then((dataUrl) => {
        const pdf = new jsPDF();
        pdf.addImage(dataUrl, 'PNG', 10, 10, 180, 240);
        pdf.save('summary-slip.pdf');
      });
  };

  return (
    <Box
      sx={{
        p: 3,
        backgroundColor: '#f4f4f4',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
      }}
    >
      <Paper
        id="summary-slip"
        elevation={3}
        sx={{
          p: 4,
          borderRadius: 4,
          maxWidth: 600,
          width: '100%',
          backgroundColor: 'white',
        }}
      >
        <Typography variant="h4" gutterBottom align="center" sx={{ fontWeight: 'bold' }}>
          Payment Summary Slip
        </Typography>

        <Divider sx={{ mb: 2 }} />

        {/* Summary for each participant */}
        {Object.entries(totalSummary).map(([participant, amountOwed]) => (
          <Box key={participant} sx={{ mb: 3 }}>
            <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
              {participant}
            </Typography>
            <Typography variant="body1" sx={{ mb: 1 }}>
              Amount Owed: <strong>{amountOwed.toFixed(2)} Baht</strong>
            </Typography>

            {/* Display food items for this participant */}
            <Typography variant="body2">
              Selected Food Items:
            </Typography>
            {foodItems
              .filter(food => food.participants.includes(participant)) // Filter items for this participant
              .map((food, index) => (
                <Typography key={index} variant="body2" sx={{ pl: 2 }}>
                  - {food.name} ({food.price.toFixed(2)} Baht)
                </Typography>
              ))}
          </Box>
        ))}

        <Divider sx={{ my: 2 }} />

        {/* QR Code Section */}
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            my: 3,
          }}
        >
          <QRCode value={qrCodeValue} size={120} />
        </Box>

        <Typography variant="body2" align="center" sx={{ color: 'gray', mb: 2 }}>
          Scan the QR code to complete the payment.
        </Typography>

        {/* Save Buttons */}
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={saveAsImage}
            >
              Save as Image
            </Button>
          </Grid>
          <Grid item xs={6}>
            <Button
              variant="contained"
              color="secondary"
              fullWidth
              onClick={saveAsPdf}
            >
              Save as PDF
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};

export default Summary;
