import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { motion } from "framer-motion";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs
import Autocomplete from "@mui/material/Autocomplete";

const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(
    loadFromLocalStorage("participants")
  );
  const [participantName, setParticipantName] = useState("");
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingFoodId, setEditingFoodId] = useState(null);

  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  const handleAddParticipant = () => {
    if (
      participantName.trim() &&
      !participants.includes(participantName.trim())
    ) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", {
        variant: "error",
      });
    }
  };

  const handleRemoveParticipant = (index) => {
    setParticipants(participants.filter((_, i) => i !== index));
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleResetFoodItems = () => {
    setFoodItems([]);
    enqueueSnackbar("All food items reset!", { variant: "info" });
  };

  const handleResetAllData = () => {
    handleResetParticipants();
    handleResetFoodItems();
    enqueueSnackbar("All data has been reset!", { variant: "info" });
  };

  const exampleFoodItems = [
    // Add your food item options here
    "เบียร์",
    "เบียร์ (โปร)",
    "เหล้า",
    "เหล้า (โปร)",
    // ... (rest of the food items)
    "ข้าวมันส้มตำ",
    "ขนมโตเกียว",
    "ผัดผักรวม",
    "ส้มตำปู",
    "ปูม้า",
    "ต้มจืด",
    "แกงเผ็ด",
    "ยำหมูยอ",
    "พริกสด",
    "น้ำผักผลไม้",
    "น้ำองุ่น",
    "น้ำมะนาวโซดา",
    "ออส่วน",
    "ข้าวเกรียบ",
    "ขนมชั้น",
    "ข้าวซอยเนื้อ",
    "น้ำมะม่วง",
    "ปีกไก่ทอดน้ำปลา",
    "แกงไก่",
    "กุ้งทอดกระเทียม",
    "ขนมจีนน้ำยาปู",
    "ข้าวต้ม",
    "ขนมปังหน้าไก่",
    "น้ำพั้นช์",
    "ห่อหมก",
    "น้ำจิ้มซีฟู้ด",
    "เกี๊ยวซ่า",
    "ผัดกระเพราเห็ด",
    "ปลาหมึกย่าง",
    "กะเพราไข่ดาว",
    "ข้าวผัดต้มยำ",
    "ขนมเกสร",
    "ซุปเห็ด",
    "หมูย่าง",
    "เส้นหมี่อบ",
    "หมี่กรอบ",
    "ข้าวไข่ข้น",
    "น้ำลูกเดือย",
    "น้ำส้ม",
    "น้ำมะพร้าวปั่น",
    "ชามะนาว",
    "ขนมช็อกโกแลต",
    "พิซซ่า",
    "ข้าวกะเพรา",
    "หมูสเต๊ะ",
  ];

  const handleAddFoodItem = () => {
    if (foodName.trim() && foodPrice > 0 && Object.keys(selectedParticipants).some(participant => selectedParticipants[participant])) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).filter(name => selectedParticipants[name]).length).toFixed(2);

      const newFoodItem = {
        id: uuidv4(),
        name: foodName,
        price: parseFloat(foodPrice),
        participants: Object.keys(selectedParticipants).filter(name => selectedParticipants[name]), // Only include selected participants
        splitPrice,
      };

      setFoodItems([...foodItems, newFoodItem]); // Add the new food item to the list
      resetFoodInputs(); // Reset the input fields
      enqueueSnackbar("Food item added!", { variant: "success" }); // Show success message
    } else {
      enqueueSnackbar("Please fill all fields correctly.", { variant: "error" }); // Show error message
    }
  };

  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(
      participants.reduce((acc, participant) => {
        acc[participant] = food.participants.includes(participant);
        return acc;
      }, {})
    );
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (
            parseFloat(foodPrice) / Object.keys(selectedParticipants).filter(name => selectedParticipants[name]).length
          ).toFixed(2);
          return {
            ...food,
            name: foodName,
            price: parseFloat(foodPrice),
            participants: Object.keys(selectedParticipants).filter(name => selectedParticipants[name]), // Update participants
            splitPrice,
          };
        }
        return food;
      });

      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box
      p={3}
      sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}
    >
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <PersonIcon /> Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{
                "& .MuiOutlinedInput-root": {
                  "& fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.12)",
                  },
                  "&:hover fieldset": {
                    borderColor: "rgba(0, 0, 0, 0.5)",
                  },
                  "&.Mui-focused fieldset": {
                    borderColor: "#FF6F61",
                  },
                },
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <List>
          {participants.map((participant, index) => (
            <ListItem key={index}>
              <ListItemText primary={participant} />
              <IconButton
                edge="end"
                onClick={() => handleRemoveParticipant(index)}
                aria-label="delete"
              >
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
        <Button
          variant="outlined"
          color="error"
          onClick={handleResetParticipants}
          sx={{ mt: 2 }}
        >
          Reset Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <Autocomplete
              freeSolo
              options={exampleFoodItems}
              renderInput={(params) => (
                <TextField {...params} label="Food Name" variant="outlined" />
              )}
              value={foodName}
              onChange={(event, newValue) => setFoodName(newValue)}
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              label="Price"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={2}>
            <Button
              variant="contained"
              color="primary"
              onClick={editingFoodId ? handleUpdateFoodItem : handleAddFoodItem}
              startIcon={<AddIcon />}
              fullWidth
            >
              {editingFoodId ? "Update" : "Add"}
            </Button>
          </Grid>
        </Grid>
        <Grid container spacing={1} sx={{ mt: 2 }}>
          {participants.map((participant) => (
            <Grid item xs={4} key={participant}>
              <Chip
                label={participant}
                onClick={() => handleSelectParticipant(participant)}
                color={selectedParticipants[participant] ? "primary" : "default"}
                variant="outlined"
              />
            </Grid>
          ))}
        </Grid>
        <List>
          {foodItems.map((food) => (
            <ListItem key={food.id}>
              <ListItemText
                primary={food.name}
                secondary={`Price: ฿${food.price.toFixed(2)} | Split Price: ฿${food.splitPrice} (Paid by: ${food.participants.join(", ")})`}
              />
              <IconButton
                edge="end"
                onClick={() => handleEditFoodItem(food)}
                aria-label="edit"
              >
                <EditIcon />
              </IconButton>
              <IconButton
                edge="end"
                onClick={() => handleRemoveFoodItem(food.id)}
                aria-label="delete"
              >
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
        <Button
          variant="outlined"
          color="error"
          onClick={handleResetFoodItems}
          sx={{ mt: 2 }}
        >
          Reset Food Items
        </Button>
      </Paper>

      {/* Summary Section */}
      <Paper elevation={3} sx={{ p: 2, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary">
          Summary
        </Typography>
        <List>
          {Object.entries(totalSummary).map(([participant, total]) => (
            <ListItem key={participant}>
              <ListItemText
                primary={participant}
                secondary={`Total: ฿${total.toFixed(2)}`}
              />
            </ListItem>
          ))}
        </List>
        <Button
          variant="contained"
          color="secondary"
          onClick={handleResetAllData}
          sx={{ mt: 2 }}
        >
          Reset All Data
        </Button>
      </Paper>
    </Box>
  );
};

export default CheckBill;
