import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, Grid, Paper, Input } from '@mui/material';
import QRCode from 'qrcode.react'; // Ensure you have this if needed
import html2canvas from 'html2canvas'; // Import html2canvas

const Summary = () => {
  const [participants, setParticipants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);
  const [qrCodeFile, setQrCodeFile] = useState(null);

  useEffect(() => {
    // Fetching data from localStorage
    const savedParticipants = JSON.parse(localStorage.getItem('participants'));
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems'));

    if (savedParticipants) setParticipants(savedParticipants);
    if (savedFoodItems) {
      setFoodItems(savedFoodItems);
      calculateTotal(savedFoodItems);
    }
  }, []);

  const calculateTotal = (items) => {
    const total = items.reduce((acc, item) => acc + item.price, 0);
    setTotalAmount(total);
  };

  const handleQrCodeChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setQrCodeFile(URL.createObjectURL(file));
    }
  };

  const saveSlipAsImage = () => {
    const slipElement = document.getElementById('summary-slip');
    html2canvas(slipElement).then(canvas => {
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'summary-slip.png';
      link.click();
    });
  };

  if (participants.length === 0 || foodItems.length === 0) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h5">กรุณากรอกข้อมูลจาก CheckBill ก่อน</Typography>
      </Box>
    );
  }

  return (
    <Box id="summary-slip" sx={{ p: 3, backgroundColor: '#f5f5f5', borderRadius: '8px', boxShadow: 3 }}>
      <Typography variant="h4" align="center" sx={{ mb: 2 }}>
        สลิปสรุป
      </Typography>
      <Paper elevation={3} sx={{ padding: 2, marginBottom: 2 }}>
        <Typography variant="h6">ยอดรวม: {totalAmount} THB</Typography>
      </Paper>
      <Grid container spacing={2}>
        {foodItems.map((item, index) => (
          <Grid item xs={12} sm={6} key={index}>
            <Paper elevation={1} sx={{ padding: 2 }}>
              <Typography variant="body1">{item.name}: {item.price} THB</Typography>
              <Typography variant="body2">ผู้เข้าร่วม: {item.participants.join(', ')}</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="h6">แนบ QR Code:</Typography>
        <Input type="file" accept="image/*" onChange={handleQrCodeChange} />
        {qrCodeFile && <img src={qrCodeFile} alt="QR Code" style={{ maxWidth: '100%', marginTop: '10px' }} />}
        <Button variant="contained" color="primary" onClick={saveSlipAsImage} sx={{ mt: 2 }}>
          เซฟเป็นรูปภาพ
        </Button>
      </Box>
    </Box>
  );
};

export default Summary;
