import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, Grid, Paper, Input } from '@mui/material';
import html2canvas from 'html2canvas';

const Summary = () => {
  const [participants, setParticipants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [qrCodeFile, setQrCodeFile] = useState(null);

  useEffect(() => {
    // Fetching data from localStorage
    const savedParticipants = JSON.parse(localStorage.getItem('participants'));
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems'));

    if (savedParticipants) setParticipants(savedParticipants);
    if (savedFoodItems) {
      setFoodItems(savedFoodItems);
      calculateTotals(savedParticipants, savedFoodItems);
    }
  }, []);

  const calculateTotals = (participants, items) => {
    const totals = {};
    const foodCost = {};

    items.forEach(item => {
      item.participants.forEach(participant => {
        if (!totals[participant]) {
          totals[participant] = { total: 0, foods: [] };
        }
        totals[participant].total += item.price;
        totals[participant].foods.push(item.name);

        // Track the cost for each food item
        if (!foodCost[item.name]) {
          foodCost[item.name] = item.price;
        }
      });
    });

    // Calculate total per participant based on shared items
    Object.keys(totals).forEach(participant => {
      const foodCount = totals[participant].foods.length;
      totals[participant].total = foodCount ? totals[participant].total / foodCount : 0; // Split total
    });

    setTotalAmounts(totals);
  };

  const handleQrCodeChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setQrCodeFile(URL.createObjectURL(file));
    }
  };

  const saveSlipAsImage = () => {
    const slipElement = document.getElementById('summary-slip');
    html2canvas(slipElement).then(canvas => {
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'summary-slip.png';
      link.click();
    });
  };

  if (Object.keys(totalAmounts).length === 0) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h5">กรุณากรอกข้อมูลจาก CheckBill ก่อน</Typography>
      </Box>
    );
  }

  return (
    <Box id="summary-slip" sx={{ p: 3, backgroundColor: '#f5f5f5', borderRadius: '8px', boxShadow: 3 }}>
      <Typography variant="h4" align="center" sx={{ mb: 2, fontWeight: 'bold' }}>
        สลิปสรุป
      </Typography>
      <Grid container spacing={2}>
        {participants.map((participant, index) => (
          <Grid item xs={12} key={index}>
            <Paper elevation={3} sx={{ padding: 2, marginBottom: 2, borderRadius: 2 }}>
              <Typography variant="h6">{participant}</Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                ยอดรวม: {totalAmounts[participant]?.total.toFixed(2) || 0} THB
              </Typography>
              <Typography variant="body2">รายการอาหาร:</Typography>
              {totalAmounts[participant]?.foods.map((food, foodIndex) => (
                <Typography variant="body2" key={foodIndex} sx={{ pl: 2 }}>
                  - {food}
                </Typography>
              ))}
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="h6">แนบ QR Code:</Typography>
        <Input type="file" accept="image/*" onChange={handleQrCodeChange} />
        {qrCodeFile && <img src={qrCodeFile} alt="QR Code" style={{ maxWidth: '100%', marginTop: '10px' }} />}
        <Button variant="contained" color="primary" onClick={saveSlipAsImage} sx={{ mt: 2 }}>
          เซฟเป็นรูปภาพ
        </Button>
      </Box>
    </Box>
  );
};

export default Summary;
