import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  IconButton,
  Snackbar,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { motion } from "framer-motion";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs

const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(loadFromLocalStorage("participants"));
  const [participantName, setParticipantName] = useState("");
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingFoodId, setEditingFoodId] = useState(null);

  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  const handleAddParticipant = () => {
    if (participantName.trim() && !participants.includes(participantName.trim())) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", { variant: "error" });
    }
  };

  const handleRemoveParticipant = (index) => {
    setParticipants(participants.filter((_, i) => i !== index));
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleAddFoodItem = () => {
    if (foodName && foodPrice > 0 && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);

      const newFoodItem = {
        id: uuidv4(),
        name: foodName,
        price: parseFloat(foodPrice),
        participants: Object.keys(selectedParticipants),
        splitPrice,
      };

      setFoodItems([...foodItems, newFoodItem]);
      resetFoodInputs();
      enqueueSnackbar("Food item added!", { variant: "success" });
    } else {
      enqueueSnackbar("Please fill all fields correctly.", { variant: "error" });
    }
  };

  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(participants.reduce((acc, participant) => {
      acc[participant] = food.participants.includes(participant);
      return acc;
    }, {}));
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
          return { ...food, name: foodName, price: parseFloat(foodPrice), participants: Object.keys(selectedParticipants), splitPrice };
        }
        return food;
      });

      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({ ...prev, [name]: !prev[name] }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box
      p={3}
      sx={{
        backgroundColor: "#f9f9f9",
        borderRadius: 2,
        boxShadow: 3,
        maxWidth: "900px",
        margin: "auto",
        position: "relative",
      }}
    >
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.6 }}>
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper
        elevation={3}
        sx={{
          p: 2,
          mb: 3,
          borderRadius: 2,
          backgroundColor: "#FF6F61",
          color: "white",
        }}
      >
        <Typography variant="h6" sx={{ mb: 2 }} color="inherit">
          <PersonIcon /> Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{ input: { color: "white" }, "& .MuiOutlinedInput-root": { "& fieldset": { borderColor: "rgba(255, 255, 255, 0.5)" }, "&:hover fieldset": { borderColor: "rgba(255, 255, 255, 0.8)" }, "&.Mui-focused fieldset": { borderColor: "white" } }}}
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              sx={{ m: 0.5, backgroundColor: "rgba(255, 255, 255, 0.8)", color: "#FF6F61" }}
              onDelete={() => handleRemoveParticipant(index)}
            />
          ))}
        </Box>
        <Button variant="outlined" color="inherit" onClick={handleResetParticipants} fullWidth>
          Reset All Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper
        elevation={3}
        sx={{
          p: 2,
          mb: 3,
          borderRadius: 2,
          backgroundColor: "#FF4B3A",
          color: "white",
        }}
      >
        <Typography variant="h6" sx={{ mb: 2 }} color="inherit">
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{ input: { color: "white" }, "& .MuiOutlinedInput-root": { "& fieldset": { borderColor: "rgba(255, 255, 255, 0.5)" }, "&:hover fieldset": { borderColor: "rgba(255, 255, 255, 0.8)" }, "&.Mui-focused fieldset": { borderColor: "white" } }}}
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Food Price"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{ input: { color: "white" }, "& .MuiOutlinedInput-root": { "& fieldset": { borderColor: "rgba(255, 255, 255, 0.5)" }, "&:hover fieldset": { borderColor: "rgba(255, 255, 255, 0.8)" }, "&.Mui-focused fieldset": { borderColor: "white" } }}}
            />
          </Grid>
        </Grid>

        <Typography variant="body1" sx={{ mt: 2 }} color="inherit">
          Select Participants:
        </Typography>
        <Box sx={{ display: "flex", flexWrap: "wrap", mb: 2 }}>
          {participants.map((participant) => (
            <Chip
              key={participant}
              label={participant}
              onClick={() => handleSelectParticipant(participant)}
              color={selectedParticipants[participant] ? "success" : "default"}
              sx={{ m: 0.5 }}
            />
          ))}
        </Box>

        <Button
          variant="contained"
          color="primary"
          onClick={editingFoodId ? handleUpdateFoodItem : handleAddFoodItem}
          startIcon={editingFoodId ? <EditIcon /> : <AddIcon />}
          fullWidth
        >
          {editingFoodId ? "Update Food Item" : "Add Food Item"}
        </Button>
      </Paper>

      {/* Food Items List Section */}
      <Typography variant="h6" align="center" gutterBottom>
        Food Items
      </Typography>
      <List>
        {foodItems.map((food) => (
          <ListItem key={food.id} sx={{ justifyContent: "space-between" }}>
            <Box>
              <Typography variant="subtitle1">{food.name}</Typography>
              <Typography variant="body2">
                ${food.price.toFixed(2)} | Split among: {food.participants.join(", ")}
              </Typography>
            </Box>
            <Box>
              <IconButton onClick={() => handleEditFoodItem(food)}>
                <EditIcon color="action" />
              </IconButton>
              <IconButton onClick={() => handleRemoveFoodItem(food.id)}>
                <DeleteIcon color="error" />
              </IconButton>
            </Box>
          </ListItem>
        ))}
      </List>

      {/* Summary Section */}
      <Paper elevation={3} sx={{ p: 2, mt: 3, borderRadius: 2, backgroundColor: "#FFC107" }}>
        <Typography variant="h6" gutterBottom align="center">
          Summary
        </Typography>
        {participants.length > 0 ? (
          <List>
            {Object.keys(totalSummary).map((participant) => (
              <ListItem key={participant}>
                <Typography variant="body1">{participant}: ${totalSummary[participant].toFixed(2)}</Typography>
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body1" align="center">
            No participants to show.
          </Typography>
        )}
      </Paper>
    </Box>
  );
};

export default CheckBill;
