import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import './Home.css'; // Import the CSS file for additional styles

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page}`);
  };

  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      height: '100vh',
      background: 'linear-gradient(135deg, #1a1a1a, #3a3a3a)',
      color: '#ffffff',
      overflow: 'hidden',
      position: 'relative',
      animation: 'backgroundFade 10s infinite alternate',
    },
    button: {
      margin: '20px',
      padding: '15px 30px',
      fontSize: '20px',
      borderRadius: '30px',
      position: 'relative',
      overflow: 'hidden',
      fontWeight: 'bold',
      color: '#ffffff',
      textTransform: 'uppercase',
      backgroundColor: '#FF7F00',
      boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
      transition: 'transform 0.3s ease, background-color 0.3s ease',
      '&:hover': {
        transform: 'scale(1.1)',
        backgroundColor: '#FF4500',
        boxShadow: '0 15px 25px rgba(255, 69, 0, 0.5)',
      },
      '&:active': {
        transform: 'scale(0.95)',
        boxShadow: '0 5px 15px rgba(0,0,0,0.2)',
      },
    },
    title: {
      marginBottom: '40px',
      fontSize: '3.5rem',
      textAlign: 'center',
      fontWeight: 'bold',
      textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)',
      animation: 'move 1.5s infinite alternate ease-in-out',
      background: 'linear-gradient(270deg, #FF0000, #FF7F00, #FFFF00, #00FF00, #00FFFF, #0000FF, #4B0082, #9400D3)',
      backgroundSize: '400% 400%',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
    },
    buttonContainer: {
      position: 'relative',
      '&:hover::after': {
        content: '""',
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '30px',
        transition: 'opacity 0.3s ease',
        opacity: '1',
      },
      '&::after': {
        content: '""',
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '30px',
        transition: 'opacity 0.3s ease',
        opacity: '0',
      },
    },
  };

  return (
    <Container style={styles.container}>
      <Typography variant="h1" style={styles.title}>
        Welcome to GlaHarn
      </Typography>
      <Grid container justifyContent="center">
        {['CheckBill', 'summary', 'statusdebtor'].map((page, index) => (
          <motion.div
            key={index}
            whileHover={{ scale: 1.05, rotate: 2 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: 'spring', stiffness: 400, damping: 10 }}
            style={styles.buttonContainer}
          >
            <Button
              variant="contained"
              style={styles.button}
              onClick={() => navigateTo(page)}
            >
              {page.charAt(0).toUpperCase() + page.slice(1).replace(/([A-Z])/g, ' $1')}
            </Button>
          </motion.div>
        ))}
      </Grid>
      <motion.img
        src="https://cdn.pixabay.com/photo/2013/07/13/10/35/moon-73728_960_720.png"
        alt="Moon"
        style={{
          position: 'absolute',
          top: '15%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '200px',
          zIndex: 1,
          filter: 'drop-shadow(0 0 30px rgba(255, 255, 255, 0.5))',
        }}
        animate={{ y: [0, -20, 0] }}
        transition={{ duration: 3, repeat: Infinity, ease: 'easeInOut' }}
      />
      {/* Optional: Add twinkling stars */}
      <div className="twinkling-stars" />
    </Container>
  );
}
