import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Container, Grid, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import './Home.css'; // CSS file for styles

export default function Home() {
  const navigate = useNavigate();

  const navigateTo = (page) => {
    navigate(`/${page}`);
  };

  return (
    <div style={{ minHeight: '100vh', position: 'relative', paddingBottom: '50px' }}> {/* container to make space for footer */}
      <Container className="home-container">
        <motion.div
          className="home-title"
          animate={{ y: [0, -15, 0] }} // Floating effect for the title
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
        >
          <Typography variant="h1">
            Welcome to GlaHarn
          </Typography>
        </motion.div>
        <Grid container spacing={5} justifyContent="center">
          {['CheckBill', 'Summary', 'StatusDebtor'].map((page, index) => (
            <Grid item key={index}>
              <motion.div
                whileHover={{ scale: 1.1, rotate: 5 }}
                whileTap={{ scale: 0.95 }}
                transition={{ type: 'spring', stiffness: 300, damping: 15 }}
              >
                <Button
                  variant="contained"
                  className="home-button"
                  onClick={() => navigateTo(page.toLowerCase())}
                >
                  {page}
                </Button>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* Add Footer here */}
      <Footer />
    </div>
  );
}
