import React from 'react';
import { useHistory } from 'react-router-dom';
import { Box, Paper, Typography, Button, Grid } from '@mui/material';

const Summary = ({ totalSummary, foodItems }) => {
  const history = useHistory();

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h4" sx={{ mb: 3 }} color="secondary">
        Summary of Expenses
      </Typography>

      {/* Total Summary of Each Participant */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="secondary">
          Participants' Total Expenses
        </Typography>
        {Object.keys(totalSummary).length === 0 ? (
          <Typography variant="subtitle1">No participants found.</Typography>
        ) : (
          <Grid container>
            {Object.keys(totalSummary).map((participant) => (
              <><Grid item xs={6} key={participant}>
                    <Typography variant="subtitle1">{participant}</Typography>
                </Grid><Grid item xs={6}>
                        <Typography variant="subtitle1">{totalSummary[participant].toFixed(2)} Baht</Typography>
                    </Grid></>
            ))}
          </Grid>
        )}
      </Paper>

      {/* Food Items Summary */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="secondary">Food Items Summary</Typography>
        {foodItems.length === 0 ? (
          <Typography variant="subtitle1">No food items added yet.</Typography>
        ) : (
          foodItems.map((food) => (
            <Box key={food.id} sx={{ mb: 2 }}>
              <Typography variant="subtitle1">
                <i>{food.name} - {food.price} Baht</i>
              </Typography>
              <Typography variant="body2">
                Shared with: {food.participants.join(", ")}
              </Typography>
            </Box>
          ))
        )}
      </Paper>

      {/* Go Back Button */}
      <Button
        variant="contained"
        color="primary"
        fullWidth
        onClick={() => history.push('/checkbill')}
        sx={{ mt: 2 }}
      >
        Back to CheckBill
      </Button>
    </Box>
  );
};

export default Summary;
