import React from "react";
import { AppBar, Toolbar, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import "./Navbar.css"; // Importing the CSS file for styles

const Navbar = () => {
  const navigate = useNavigate();

  // Define custom styles for each path
  const buttonStyles = {
    home: {
      // backgroundColor: "#FF6F61", // Custom style for Home button
    },
    checkbill: {
      // backgroundColor: "#6A994E", // Custom style for /checkbill button
    },
    summary: {
      // backgroundColor: "#BC4749", // Custom style for /summary button
    },
    statusdebtor: {
      backgroundColor: "#0077B6", // Custom style for /statusdebtor button
    },
  };

  return (
    <AppBar
      position="static"
      sx={{
        mb: 4,
        backgroundColor: "#4A4E69",
        boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
        padding: { xs: "8px", sm: "16px" },
      }}
    >
      <Toolbar>
        {/* Home Button */}
        <motion.div whileHover={{ scale: 1.1 }}>
          <Button
            color="inherit"
            onClick={() => navigate("/")}
            sx={{
              ...buttonStyles.home, // Apply custom style for Home button
              color: "#F9F7F7",
              textTransform: "uppercase",
              fontWeight: "500",
              marginRight: { xs: 1, sm: 2 },
              fontSize: { xs: "0.8rem", sm: "1rem" },
              "&:hover": {
                color: "#4A4E69",
                backgroundColor: "#F9F7F7",
                transition: "0.3s",
                boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
              },
            }}
          >
            Home
          </Button>
        </motion.div>

        {/* Dynamic Buttons */}
        {["/checkbill", "/summary", "/statusdebtor"].map((path, index) => {
          const pageName =
            path.slice(1).charAt(0).toUpperCase() + path.slice(2); // Format label
          const styleKey = pageName.toLowerCase(); // Get style key for each button

          return (
            <motion.div key={index} whileHover={{ scale: 1.1 }}>
              <Button
                color="inherit"
                onClick={() => navigate(path)}
                sx={{
                  ...buttonStyles[styleKey], // Apply specific style for each button
                  color: "#F9F7F7",
                  textTransform: "uppercase",
                  fontWeight: "500",
                  fontSize: { xs: "0.8rem", sm: "1rem" },
                  "&:hover": {
                    color: "#4A4E69",
                    backgroundColor: "#F9F7F7",
                    transition: "0.3s",
                    boxShadow: "0 4px 20px rgba(0,0,0,0.2)",
                  },
                }}
              >
                {pageName}
              </Button>
            </motion.div>
          );
        })}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
