import React, { useState, useEffect } from "react";
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Paper,
  TextField,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Button,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";

export default function StatusDebtor() {
  const [participants, setParticipants] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [payments, setPayments] = useState({});
  const [status, setStatus] = useState({});
  const [paymentHistory, setPaymentHistory] = useState({});
  const [paymentInput, setPaymentInput] = useState({});

  // Fetch stored data from localStorage when the component mounts
  useEffect(() => {
    const storedParticipants = JSON.parse(localStorage.getItem("participants")) || [];
    const storedTotalAmounts = JSON.parse(localStorage.getItem("totalAmounts")) || {};
    const storedPayments = JSON.parse(localStorage.getItem("payments")) || {};
    const storedHistory = JSON.parse(localStorage.getItem("paymentHistory")) || {};

    setParticipants(storedParticipants);
    setTotalAmounts(storedTotalAmounts);
    setPayments(storedPayments);
    setPaymentHistory(storedHistory);

    const initialStatus = {};
    storedParticipants.forEach((participant) => {
      initialStatus[participant] = "Pending"; // Set initial status to "Pending"
    });
    setStatus(initialStatus);
  }, []);

  // Handle input change for payment but don't update yet
  const handleInputChange = (participant, amount) => {
    setPaymentInput((prevInput) => ({
      ...prevInput,
      [participant]: amount,
    }));
  };

  // Handle payment update on button click
  const handleUpdatePayment = (participant) => {
    const amount = parseFloat(paymentInput[participant]);
    if (isNaN(amount)) {
      alert("Please enter a valid number.");
      return;
    }

    const updatedPayments = {
      ...payments,
      [participant]: amount,
    };
    setPayments(updatedPayments);
    localStorage.setItem("payments", JSON.stringify(updatedPayments));

    // Record payment with the date
    const newPaymentHistory = { ...paymentHistory };
    if (!newPaymentHistory[participant]) {
      newPaymentHistory[participant] = [];
    }
    newPaymentHistory[participant].push({
      amount: amount,
      date: new Date().toLocaleDateString(),
    });
    setPaymentHistory(newPaymentHistory);
    localStorage.setItem("paymentHistory", JSON.stringify(newPaymentHistory));

    // Update status based on payment
    if (updatedPayments[participant] >= (totalAmounts[participant]?.total || 0)) {
      setStatus((prevStatus) => ({
        ...prevStatus,
        [participant]: "Paid",
      }));
    } else {
      setStatus((prevStatus) => ({
        ...prevStatus,
        [participant]: "Pending",
      }));
    }

    // Clear input after submission
    setPaymentInput((prevInput) => ({
      ...prevInput,
      [participant]: "",
    }));
  };

  // Calculate remaining amount for each participant
  const calculateRemaining = (participant) => {
    const totalAmount = totalAmounts[participant]?.total || 0;
    const paidAmount = payments[participant] || 0;
    return totalAmount - paidAmount;
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Status of Debtors
      </Typography>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Participant</TableCell>
              <TableCell>Amount Owed</TableCell>
              <TableCell>Amount Paid</TableCell>
              <TableCell>Remaining</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Payment History</TableCell>
              <TableCell>Update Payment</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {participants.map((participant) => (
              <TableRow key={participant}>
                <TableCell>{participant}</TableCell>
                <TableCell>
                  {totalAmounts[participant]?.total
                    ? totalAmounts[participant].total.toFixed(2) + " ฿"
                    : "0.00 ฿"}
                </TableCell>
                <TableCell>
                  {payments[participant] ? payments[participant].toFixed(2) : "0.00 ฿"}
                </TableCell>
                <TableCell>
                  {calculateRemaining(participant).toFixed(2)} ฿
                </TableCell>
                <TableCell>{status[participant]}</TableCell>
                <TableCell>
                  <Accordion>
                    <AccordionSummary
                      expandIcon={<ExpandMoreIcon />}
                      aria-controls="panel1a-content"
                      id="panel1a-header"
                    >
                      <Typography>History</Typography>
                    </AccordionSummary>
                    <AccordionDetails>
                      {paymentHistory[participant]?.length > 0 ? (
                        paymentHistory[participant].map((payment, index) => (
                          <Typography key={index}>
                            {payment.date}: {payment.amount.toFixed(2)} ฿
                          </Typography>
                        ))
                      ) : (
                        <Typography>No history available</Typography>
                      )}
                    </AccordionDetails>
                  </Accordion>
                </TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    label="Payment"
                    variant="outlined"
                    size="small"
                    value={paymentInput[participant] || ""}
                    onChange={(e) => handleInputChange(participant, e.target.value)}
                  />
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleUpdatePayment(participant)}
                    sx={{ ml: 1 }}
                  >
                    Update Payment
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}
