import React, { useState, useEffect } from 'react';
import {
  TextField, Button, List, ListItem, Checkbox, Typography, Grid, Box, IconButton,
  Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { motion } from 'framer-motion';

// Load from LocalStorage
const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

// Save to LocalStorage
const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const [participants, setParticipants] = useState(loadFromLocalStorage('participants'));
  const [participantName, setParticipantName] = useState('');
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage('foodItems'));
  const [foodName, setFoodName] = useState('');
  const [foodPrice, setFoodPrice] = useState('');
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingIndex, setEditingIndex] = useState(null);
  const [editFoodIndex, setEditFoodIndex] = useState(null);

  // Save data to LocalStorage
  useEffect(() => {
    saveToLocalStorage('participants', participants);
    saveToLocalStorage('foodItems', foodItems);
  }, [participants, foodItems]);

  // Add a new participant
  const handleAddParticipant = () => {
    if (participantName) {
      setParticipants([...participants, participantName]);
      setParticipantName('');
    }
  };

  // Reset all participants
  const handleResetParticipants = () => {
    setParticipants([]);
    setSelectedParticipants({});
  };

  // Add a food item
  const handleAddFoodItem = () => {
    if (foodName && foodPrice && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
      setFoodItems([
        ...foodItems,
        {
          name: foodName,
          price: parseFloat(foodPrice),
          participants: Object.keys(selectedParticipants),
          splitPrice,
        },
      ]);
      setFoodName('');
      setFoodPrice('');
      setSelectedParticipants({});
    }
  };

  // Reset all food items
  const handleResetFoodItems = () => {
    setFoodItems([]);
  };

  // Select or deselect a participant for splitting the bill
  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  // Open edit dialog for a participant
  const handleEditParticipant = (index) => {
    setParticipantName(participants[index]);
    setEditingIndex(index);
  };

  // Save the edited participant name
  const handleSaveEditParticipant = () => {
    if (editingIndex !== null) {
      const updatedParticipants = [...participants];
      updatedParticipants[editingIndex] = participantName;
      setParticipants(updatedParticipants);
      setParticipantName('');
      setEditingIndex(null);
    }
  };

  // Open edit dialog for food item
  const handleEditFoodItem = (index) => {
    setFoodName(foodItems[index].name);
    setFoodPrice(foodItems[index].price);
    setEditFoodIndex(index);
  };

  // Save the edited food item
  const handleSaveEditFoodItem = () => {
    if (editFoodIndex !== null) {
      const updatedFoodItems = [...foodItems];
      updatedFoodItems[editFoodIndex] = { ...updatedFoodItems[editFoodIndex], name: foodName, price: parseFloat(foodPrice) };
      setFoodItems(updatedFoodItems);
      setFoodName('');
      setFoodPrice('');
      setEditFoodIndex(null);
    }
  };

  // Calculate total amount each participant owes
  const calculateTotals = () => {
    const totals = {};
    foodItems.forEach((food) => {
      food.participants.forEach((participant) => {
        totals[participant] = (totals[participant] || 0) + parseFloat(food.splitPrice);
      });
    });
    return totals;
  };

  const totals = calculateTotals();

  return (
    <Box p={2}>
      <Typography variant="h4" gutterBottom>Check Bill</Typography>

      {/* Add Participants Section */}
      <Typography variant="h6">Add/Edit Participants</Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={8}>
          <TextField
            label="Participant Name"
            value={participantName}
            onChange={(e) => setParticipantName(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={4}>
          {editingIndex === null ? (
            <Button variant="contained" color="primary" onClick={handleAddParticipant}>Add</Button>
          ) : (
            <Button variant="contained" color="secondary" onClick={handleSaveEditParticipant}>Save</Button>
          )}
        </Grid>
      </Grid>

      {/* Scrollable Participant List */}
      <Box mt={2} mb={2} maxHeight="150px" overflow="auto">
        <List>
          {participants.map((name, index) => (
            <ListItem key={index}>
              {name}
              <IconButton onClick={() => handleEditParticipant(index)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => setParticipants(participants.filter((_, i) => i !== index))}>
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </Box>
      <Button variant="outlined" color="secondary" onClick={handleResetParticipants}>Reset All Participants</Button>

      {/* Add Food Items Section */}
      <Typography variant="h6" mt={3}>Add/Edit Food Items</Typography>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={6}>
          <TextField
            label="Food Name"
            value={foodName}
            onChange={(e) => setFoodName(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Price"
            type="number"
            value={foodPrice}
            onChange={(e) => setFoodPrice(e.target.value)}
            fullWidth
          />
        </Grid>
      </Grid>

      {/* Scrollable Food Items Section */}
      <Box mt={2} mb={2} maxHeight="150px" overflow="auto">
        <List>
          {foodItems.map((food, index) => (
            <ListItem key={index}>
              {`${food.name} - $${food.price} split by ${food.participants?.join(', ')}. $${food.splitPrice} per person`}
              <IconButton onClick={() => handleEditFoodItem(index)}>
                <EditIcon />
              </IconButton>
              <IconButton onClick={() => setFoodItems(foodItems.filter((_, i) => i !== index))}>
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </Box>
      <Button variant="contained" color="primary" onClick={handleAddFoodItem}>Add Food</Button>
      <Button variant="outlined" color="secondary" onClick={handleResetFoodItems} style={{ marginLeft: '10px' }}>Reset All Food Items</Button>

      {/* Summary Section */}
      <Typography variant="h6" mt={3}>Summary</Typography>
      <List>
        {Object.keys(totals).map((participant, index) => (
          <ListItem key={index}>
            <strong>{participant}</strong> owes <strong>${totals[participant].toFixed(2)}</strong> for: 
            <ul>
              {foodItems.filter(food => food.participants.includes(participant)).map((food, i) => (
                <li key={i}><em>{food.name}</em></li>
              ))}
            </ul>
          </ListItem>
        ))}
      </List>

      <motion.div whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
        <Button variant="contained" color="success" style={{ marginTop: '20px' }}>Make Slip</Button>
      </motion.div>
    </Box>
  );
};

export default CheckBill;
