import React, { useState, useEffect } from 'react';
import { Box, Button, TextField, Typography, Paper, Grid } from '@mui/material';
import { motion } from 'framer-motion';

const CheckBill = () => {
  const [participants, setParticipants] = useState([]);
  const [newParticipant, setNewParticipant] = useState('');
  const [foodItems, setFoodItems] = useState([]);
  const [newFoodItem, setNewFoodItem] = useState({ name: '', price: '', participants: [] });

  // Load from localStorage on component mount
  useEffect(() => {
    const savedParticipants = JSON.parse(localStorage.getItem('participants')) || [];
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems')) || [];
    setParticipants(savedParticipants);
    setFoodItems(savedFoodItems);
  }, []);

  // Save participants and foodItems to localStorage on update
  useEffect(() => {
    localStorage.setItem('participants', JSON.stringify(participants));
    localStorage.setItem('foodItems', JSON.stringify(foodItems));
  }, [participants, foodItems]);

  const handleAddParticipant = () => {
    if (newParticipant && !participants.includes(newParticipant)) {
      setParticipants([...participants, newParticipant]);
      setNewParticipant('');
    }
  };

  const handleAddFoodItem = () => {
    if (newFoodItem.name && newFoodItem.price && newFoodItem.participants.length > 0) {
      setFoodItems([...foodItems, { ...newFoodItem, id: Date.now() }]);
      setNewFoodItem({ name: '', price: '', participants: [] });
    }
  };

  const handleToggleParticipantInFood = (participant) => {
    setNewFoodItem((prevItem) => ({
      ...prevItem,
      participants: prevItem.participants.includes(participant)
        ? prevItem.participants.filter((p) => p !== participant)
        : [...prevItem.participants, participant],
    }));
  };

  const handleResetAll = () => {
    setParticipants([]);
    setFoodItems([]);
    localStorage.removeItem('participants');
    localStorage.removeItem('foodItems');
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }} color="secondary">
        Check Bill
      </Typography>

      {/* Add Participant Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="primary">
          Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              fullWidth
              label="Participant Name"
              value={newParticipant}
              onChange={(e) => setNewParticipant(e.target.value)}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleAddParticipant}
            >
              Add Participant
            </Button>
          </Grid>
        </Grid>

        {/* Participant List */}
        {participants.length > 0 && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1" color="secondary">Participants:</Typography>
            <motion.div layout>
              {participants.map((participant) => (
                <Typography key={participant} variant="body1">
                  {participant}
                </Typography>
              ))}
            </motion.div>
          </Box>
        )}
      </Paper>

      {/* Add Food Item Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="primary">
          Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={4}>
            <TextField
              fullWidth
              label="Food Name"
              value={newFoodItem.name}
              onChange={(e) => setNewFoodItem({ ...newFoodItem, name: e.target.value })}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={4}>
            <TextField
              fullWidth
              label="Price (Baht)"
              type="number"
              value={newFoodItem.price}
              onChange={(e) => setNewFoodItem({ ...newFoodItem, price: e.target.value })}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleAddFoodItem}
            >
              Add Food Item
            </Button>
          </Grid>
        </Grid>

        {/* Select Participants for Food Item */}
        {participants.length > 0 && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1" color="secondary">
              Select Participants Sharing This Food:
            </Typography>
            <Grid container spacing={1}>
              {participants.map((participant) => (
                <Grid item key={participant}>
                  <Button
                    variant={
                      newFoodItem.participants.includes(participant)
                        ? 'contained'
                        : 'outlined'
                    }
                    color="secondary"
                    onClick={() => handleToggleParticipantInFood(participant)}
                  >
                    {participant}
                  </Button>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}
      </Paper>

      {/* Food Items List */}
      {foodItems.length > 0 && (
        <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
          <Typography variant="h6" color="primary">Food Items List</Typography>
          {foodItems.map((food) => (
            <Box key={food.id} sx={{ mb: 2 }}>
              <Typography variant="subtitle1">
                <i>{food.name} - {food.price} Baht</i>
              </Typography>
              <Typography variant="body2">
                Shared with: {food.participants.join(", ")}
              </Typography>
            </Box>
          ))}
        </Paper>
      )}

      {/* Reset All Button */}
      <Button
        variant="contained"
        color="secondary"
        fullWidth
        onClick={handleResetAll}
        sx={{ mb: 2 }}
      >
        Reset All
      </Button>

      {/* Go to Summary Button */}
      <Button
        variant="contained"
        color="primary"
        fullWidth
        onClick={() => window.location.href = '/summary'}
      >
        Make Slip Summary
      </Button>
    </Box>
  );
};

export default CheckBill;
