import React, { useEffect, useState } from "react";
import {
  Box,
  Button,
  Typography,
  List,
  ListItem,
  IconButton,
  Paper,
  Grid,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import { useSnackbar } from "notistack";

const StatusDebtor = () => {
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(() => {
    const savedParticipants = localStorage.getItem("participants");
    return savedParticipants ? JSON.parse(savedParticipants) : [];
  });
  const [debts, setDebts] = useState(() => {
    const savedDebts = localStorage.getItem("debts");
    return savedDebts ? JSON.parse(savedDebts) : {};
  });

  useEffect(() => {
    localStorage.setItem("participants", JSON.stringify(participants));
    localStorage.setItem("debts", JSON.stringify(debts));
  }, [participants, debts]);

  const handleAddDebt = (participant) => {
    const amount = prompt("Enter amount for " + participant);
    if (amount && !isNaN(amount) && amount > 0) {
      setDebts((prev) => ({
        ...prev,
        [participant]: (prev[participant] || 0) + parseFloat(amount),
      }));
      enqueueSnackbar(`Added debt of ${amount} ฿ for ${participant}`, {
        variant: "success",
      });
    } else {
      enqueueSnackbar("Invalid amount entered.", { variant: "error" });
    }
  };

  const handleRemoveDebt = (participant) => {
    const currentDebt = debts[participant];
    if (currentDebt) {
      const amount = prompt(
        `Enter amount paid by ${participant} (current debt: ${currentDebt.toFixed(2)} ฿)`
      );
      if (amount && !isNaN(amount) && amount > 0 && amount <= currentDebt) {
        setDebts((prev) => ({
          ...prev,
          [participant]: prev[participant] - parseFloat(amount),
        }));
        enqueueSnackbar(`Removed debt of ${amount} ฿ for ${participant}`, {
          variant: "success",
        });
      } else {
        enqueueSnackbar("Invalid amount entered.", { variant: "error" });
      }
    } else {
      enqueueSnackbar(`No debt found for ${participant}.`, {
        variant: "info",
      });
    }
  };

  const handleResetDebts = () => {
    setDebts({});
    enqueueSnackbar("All debts reset!", { variant: "info" });
  };

  return (
    <Box
      p={3}
      sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}
    >
      <Typography variant="h4" align="center" gutterBottom color="primary">
        Status Debtor
      </Typography>

      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2 }}>
        <Typography variant="h6" color="secondary" sx={{ mb: 2 }}>
          Participants
        </Typography>
        <List>
          {participants.map((participant) => (
            <ListItem key={participant} sx={{ display: "flex", justifyContent: "space-between" }}>
              <Typography variant="body1">{participant}</Typography>
              <Box>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleAddDebt(participant)}
                  sx={{ mr: 1 }}
                >
                  Add Debt
                </Button>
                <IconButton
                  color="error"
                  onClick={() => handleRemoveDebt(participant)}
                >
                  <DeleteIcon />
                </IconButton>
              </Box>
            </ListItem>
          ))}
        </List>
      </Paper>

      <Typography variant="h5" sx={{ mb: 2 }} color="secondary">
        Total Debts
      </Typography>
      {Object.keys(debts).map((participant) => (
        <Typography key={participant} variant="body1">
          {participant}: {debts[participant].toFixed(2)} ฿
        </Typography>
      ))}

      <Button
        variant="outlined"
        color="error"
        onClick={handleResetDebts}
        fullWidth
        sx={{ mt: 2 }}
      >
        Reset All Debts
      </Button>
    </Box>
  );
};

export default StatusDebtor;
