import React from 'react';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Navbar.css'; // Ensure you have custom styles here

const Navbar = () => {
  const navigate = useNavigate();

  return (
    <AppBar position="static" sx={{ mb: 4, backgroundColor: '#FF6F61', boxShadow: '0 4px 20px rgba(0,0,0,0.3)' }}>
      <Toolbar>
        <Typography variant="h5" sx={{ flexGrow: 1, fontWeight: 'bold', color: 'white', letterSpacing: 2 }}>
          GlaHarn
        </Typography>
        {['/', '/checkbill', '/summary', '/statusdebtor'].map((path, index) => {
          const pageName = path === '/' ? 'Home' : path.slice(1).charAt(0).toUpperCase() + path.slice(2);
          return (
            <motion.div 
              key={index} 
              whileHover={{ scale: 1.1, rotate: 5 }} 
              transition={{ type: 'spring', stiffness: 300 }}
              style={{ marginLeft: '16px' }}
            >
              <Button 
                color="inherit" 
                onClick={() => navigate(path)} 
                sx={{
                  position: 'relative',
                  overflow: 'hidden',
                  fontSize: '1.1rem',
                  textTransform: 'uppercase',
                  transition: 'color 0.3s ease, background-color 0.3s ease',
                  '&:hover': {
                    color: '#FF6F61',
                    backgroundColor: 'white',
                    boxShadow: '0 4px 20px rgba(0,0,0,0.2)',
                  },
                }}
              >
                {pageName}
                <motion.div
                  className="hover-effect"
                  initial={{ scaleX: 0 }}
                  animate={{ scaleX: 1 }}
                  exit={{ scaleX: 0 }}
                  transition={{ type: 'spring', stiffness: 300 }}
                />
              </Button>
            </motion.div>
          );
        })}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
