import React, { useState, useEffect } from 'react';
import { TextField, Button, List, ListItem, Checkbox, Typography, Grid, Box, Paper } from '@mui/material';
import { motion } from 'framer-motion';

// Function to load from LocalStorage
const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

// Function to save to LocalStorage
const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const [participants, setParticipants] = useState(loadFromLocalStorage('participants'));
  const [participantName, setParticipantName] = useState('');
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage('foodItems'));
  const [foodName, setFoodName] = useState('');
  const [foodPrice, setFoodPrice] = useState('');
  const [selectedParticipants, setSelectedParticipants] = useState({});

  // Save participants and food items to LocalStorage whenever they change
  useEffect(() => {
    saveToLocalStorage('participants', participants);
    saveToLocalStorage('foodItems', foodItems);
  }, [participants, foodItems]);

  // Add participant
  const handleAddParticipant = () => {
    if (participantName) {
      setParticipants([...participants, participantName]);
      setParticipantName('');
    }
  };

  // Reset participants
  const handleResetParticipants = () => {
    setParticipants([]);
    setSelectedParticipants({});
  };

  // Add food item
  const handleAddFoodItem = () => {
    if (foodName && foodPrice && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
      setFoodItems([
        ...foodItems,
        {
          name: foodName,
          price: parseFloat(foodPrice),
          participants: Object.keys(selectedParticipants),
          splitPrice,
        },
      ]);
      setFoodName('');
      setFoodPrice('');
      setSelectedParticipants({});
    }
  };

  // Reset food items
  const handleResetFoodItems = () => {
    setFoodItems([]);
  };

  // Toggle participant selection for splitting the bill
  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  return (
    <Box sx={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      {/* Main Container */}
      <Paper elevation={3} sx={{ padding: '30px', borderRadius: '15px', backgroundColor: '#fdfdfd' }}>

        {/* Title */}
        <Typography variant="h4" gutterBottom align="center" color="primary" sx={{ fontWeight: 'bold' }}>
          Check Bill
        </Typography>

        {/* Add Participants Section */}
        <Typography variant="h6" sx={{ marginTop: '20px' }}>Add Participants</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  '&.Mui-focused fieldset': { borderColor: '#ff9800' }
                }
              }}
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="secondary"
              fullWidth
              onClick={handleAddParticipant}
              component={motion.div}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              sx={{
                fontWeight: 'bold',
                backgroundColor: '#ff9800',
                '&:hover': { backgroundColor: '#ffb74d' }
              }}
            >
              Add
            </Button>
          </Grid>
        </Grid>

        {/* Participants List */}
        <List sx={{ marginTop: '15px' }}>
          {participants.map((name, index) => (
            <ListItem key={index} sx={{ backgroundColor: '#f5f5f5', marginBottom: '5px', borderRadius: '10px' }}>
              {name}
            </ListItem>
          ))}
        </List>
        <Button
          variant="outlined"
          color="error"
          onClick={handleResetParticipants}
          sx={{
            marginTop: '10px',
            fontWeight: 'bold',
            '&:hover': { backgroundColor: '#fce4ec' }
          }}
        >
          Reset All Participants
        </Button>

        {/* Add Food Items Section */}
        <Typography variant="h6" sx={{ marginTop: '30px' }}>Add Food Items</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
        </Grid>

        {/* Select Participants */}
        <Typography variant="subtitle1" sx={{ marginTop: '10px' }}>
          Select Participants to Split the Bill:
        </Typography>
        <List sx={{ maxHeight: '150px', overflow: 'auto' }}>
          {participants.map((name, index) => (
            <ListItem key={index}>
              <Checkbox
                checked={!!selectedParticipants[name]}
                onChange={() => handleSelectParticipant(name)}
                color="primary"
              />
              {name}
            </ListItem>
          ))}
        </List>
        <Button
          variant="contained"
          color="primary"
          onClick={handleAddFoodItem}
          component={motion.div}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          sx={{ marginTop: '10px', fontWeight: 'bold' }}
        >
          Add Food
        </Button>

        {/* List of Food Items */}
        <Typography variant="h6" sx={{ marginTop: '30px' }}>Food List</Typography>
        <List>
          {foodItems.map((food, index) => (
            <ListItem key={index} sx={{ backgroundColor: '#f5f5f5', marginBottom: '5px', borderRadius: '10px' }}>
              {`${food.name} - $${food.price} split by ${food.participants?.join(', ')}. $${food.splitPrice} per person`}
            </ListItem>
          ))}
        </List>
        <Button
          variant="outlined"
          color="error"
          onClick={handleResetFoodItems}
          sx={{ marginTop: '10px', fontWeight: 'bold', '&:hover': { backgroundColor: '#fce4ec' } }}
        >
          Reset All Food Items
        </Button>

        {/* Summary Button */}
        <motion.div whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
          <Button
            variant="contained"
            color="success"
            fullWidth
            sx={{ marginTop: '30px', fontWeight: 'bold', padding: '10px', fontSize: '18px' }}
          >
            Make Slip
          </Button>
        </motion.div>

      </Paper>
    </Box>
  );
};

export default CheckBill;
