import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  IconButton,
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { motion } from "framer-motion";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs

// Utility functions for localStorage
const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const { enqueueSnackbar } = useSnackbar();

  // State hooks for participants and food items
  const [participants, setParticipants] = useState(loadFromLocalStorage("participants"));
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [participantName, setParticipantName] = useState("");
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});
  const [editingFoodId, setEditingFoodId] = useState(null);

  // Effect hook to persist data to localStorage
  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  // Handler to add a participant
  const handleAddParticipant = () => {
    const trimmedName = participantName.trim();
    if (trimmedName && !participants.includes(trimmedName)) {
      setParticipants([...participants, trimmedName]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", { variant: "error" });
    }
  };

  // Handler to remove a participant
  const handleRemoveParticipant = (index) => {
    const updatedParticipants = participants.filter((_, i) => i !== index);
    setParticipants(updatedParticipants);
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  // Reset functions for participants and food items
  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleResetFoodItems = () => {
    setFoodItems([]);
    enqueueSnackbar("All food items reset!", { variant: "info" });
  };

  const handleResetAllData = () => {
    handleResetParticipants();
    handleResetFoodItems();
    enqueueSnackbar("All data has been reset!", { variant: "info" });
  };

  // Handler to add food item
  const handleAddFoodItem = () => {
    if (foodName && foodPrice > 0 && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
      const newFoodItem = {
        id: uuidv4(),
        name: foodName,
        price: parseFloat(foodPrice),
        participants: Object.keys(selectedParticipants),
        splitPrice,
      };
      setFoodItems([...foodItems, newFoodItem]);
      resetFoodInputs();
      enqueueSnackbar("Food item added!", { variant: "success" });
    } else {
      enqueueSnackbar("Please fill all fields correctly.", { variant: "error" });
    }
  };

  // Resets food input fields
  const resetFoodInputs = () => {
    setFoodName("");
    setFoodPrice("");
    setSelectedParticipants({});
    setEditingFoodId(null);
  };

  // Handlers for editing food items
  const handleEditFoodItem = (food) => {
    setFoodName(food.name);
    setFoodPrice(food.price);
    setSelectedParticipants(participants.reduce((acc, participant) => {
      acc[participant] = food.participants.includes(participant);
      return acc;
    }, {}));
    setEditingFoodId(food.id);
  };

  const handleUpdateFoodItem = () => {
    if (editingFoodId) {
      const updatedFoodItems = foodItems.map((food) => {
        if (food.id === editingFoodId) {
          const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
          return {
            ...food,
            name: foodName,
            price: parseFloat(foodPrice),
            participants: Object.keys(selectedParticipants),
            splitPrice,
          };
        }
        return food;
      });
      setFoodItems(updatedFoodItems);
      resetFoodInputs();
      enqueueSnackbar("Food item updated!", { variant: "success" });
    }
  };

  // Handler to remove a food item
  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  // Handler for participant selection
  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  // Calculate total summary for participants
  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box p={3} sx={{ backgroundColor: "#f9f9f9", borderRadius: 2, boxShadow: 3 }}>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.6 }}>
        <Typography variant="h4" align="center" gutterBottom color="primary">Check Bill</Typography>
      </motion.div>

      {/* Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary"><PersonIcon /> Add Participants</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              sx={{
                m: 0.5,
                backgroundColor: "#FF6F61",
                color: "white",
                "&:hover": { backgroundColor: "#FF4B3A" },
              }}
              onDelete={() => handleRemoveParticipant(index)}
            />
          ))}
        </Box>
        <Button variant="outlined" color="secondary" onClick={handleResetParticipants} fullWidth>
          Reset All Participants
        </Button>
      </Paper>

      {/* Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" sx={{ mb: 2 }} color="secondary"><RestaurantMenuIcon /> Add Food Items</Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price (฿)"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              inputProps={{ min: 0 }} // Prevent negative prices
              variant="outlined"
            />
          </Grid>
        </Grid>
        <Typography variant="subtitle1" sx={{ mt: 2 }}>Select Participants to Split the Bill:</Typography>
        <Box sx={{ display: "flex", flexWrap: "wrap", mt: 1 }}>
          {participants.map((name) => (
            <Chip
              key={name}
              label={name}
              onClick={() => handleSelectParticipant(name)}
              sx={{
                m: 0.5,
                backgroundColor: selectedParticipants[name] ? "#FF6F61" : "rgba(0, 0, 0, 0.12)",
                color: selectedParticipants[name] ? "white" : "black",
                "&:hover": { cursor: "pointer", backgroundColor: selectedParticipants[name] ? "#FF4B3A" : "rgba(0, 0, 0, 0.2)" },
              }}
            />
          ))}
        </Box>
        <Button
          variant="contained"
          color="primary"
          onClick={editingFoodId ? handleUpdateFoodItem : handleAddFoodItem}
          startIcon={<AddIcon />}
          fullWidth
          sx={{ mt: 2 }}
        >
          {editingFoodId ? "Update Food Item" : "Add Food Item"}
        </Button>
        <Button variant="outlined" color="secondary" onClick={handleResetFoodItems} fullWidth sx={{ mt: 1 }}>
          Reset All Food Items
        </Button>
      </Paper>

      {/* Food Items List */}
      <Paper elevation={3} sx={{ p: 2, mb: 3, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="secondary">Food Items List</Typography>
        <List>
          {foodItems.map((food) => (
            <ListItem key={food.id} sx={{ justifyContent: "space-between", alignItems: "center" }}>
              <Typography variant="body1">{food.name} (฿{food.price.toFixed(2)})</Typography>
              <Box>
                <IconButton onClick={() => handleEditFoodItem(food)}><EditIcon /></IconButton>
                <IconButton onClick={() => handleRemoveFoodItem(food.id)}><DeleteIcon /></IconButton>
              </Box>
            </ListItem>
          ))}
        </List>
      </Paper>

      {/* Summary Section */}
      <Paper elevation={3} sx={{ p: 2, borderRadius: 2, boxShadow: 5 }}>
        <Typography variant="h6" color="secondary">Summary of Costs</Typography>
        {participants.length === 0 ? (
          <Typography variant="body2" color="textSecondary">No participants to show.</Typography>
        ) : (
          <List>
            {participants.map((participant) => (
              <ListItem key={participant} sx={{ justifyContent: "space-between" }}>
                <Typography variant="body1">{participant}</Typography>
                <Typography variant="body1">฿{(totalSummary[participant] || 0).toFixed(2)}</Typography>
              </ListItem>
            ))}
          </List>
        )}
        <Button variant="outlined" color="secondary" onClick={handleResetAllData} fullWidth sx={{ mt: 2 }}>
          Reset All Data
        </Button>
      </Paper>
    </Box>
  );
};

export default CheckBill;
