import React from 'react';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Navbar.css';

const Navbar = () => {
  const navigate = useNavigate();

  return (
    <AppBar position="static" sx={{ mb: 4, backgroundColor: '#FF6F61', boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)' }}>
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 'bold', letterSpacing: 2 }}>
          GlaHarn
        </Typography>
        {['/', '/checkbill', '/summary', '/statusdebtor'].map((path, index) => {
          const pageName = path === '/' ? 'Home' : path.slice(1).charAt(0).toUpperCase() + path.slice(2);
          return (
            <motion.div key={index} whileHover={{ scale: 1.1 }} transition={{ duration: 0.3 }}>
              <Button
                color="inherit"
                onClick={() => navigate(path)}
                className="navbar-button"
                sx={{
                  position: 'relative',
                  overflow: 'hidden',
                  transition: 'color 0.3s',
                  '&:hover': {
                    color: '#FF6F61',
                    backgroundColor: 'white',
                  },
                }}
              >
                {pageName}
              </Button>
            </motion.div>
          );
        })}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
