    <Box id="summary-slip" sx={{ p: 3, background: 'linear-gradient(to bottom right, #e0f7fa, #fce4ec)', borderRadius: '16px', boxShadow: 5, maxWidth: '600px', mx: 'auto', mt: 4 }}>
      <Typography variant="h3" align="center" sx={{ mb: 3, fontWeight: 'bold', color: '#3f51b5' }}>
        üßæ ‡∏™‡∏•‡∏¥‡∏õ‡∏™‡∏£‡∏∏‡∏õ
      </Typography>
      <Grid container spacing={2}>
        {participants.map((participant, index) => (
          <Grid item xs={12} key={index}>
            <Paper elevation={5} sx={{ padding: 3, marginBottom: 2, borderRadius: 4, backgroundColor: '#ffffff', border: '2px solid #3f51b5' }}>
              <Typography variant="h5" sx={{ color: '#3f51b5', fontWeight: 'bold' }}>{participant}</Typography>
              <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#f44336' }}>
                ‡∏¢‡∏≠‡∏î‡∏£‡∏ß‡∏°: {totalAmounts[participant]?.total.toFixed(2) || 0} B
              </Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold', marginBottom: 1 }}>‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏≠‡∏≤‡∏´‡∏≤‡∏£:</Typography>
              {totalAmounts[participant]?.foods.map((food, foodIndex) => (
                <Typography variant="body2" key={foodIndex} sx={{ pl: 2, color: '#555' }}>
                  - {food.name}: {food.price.toFixed(2)} B
                </Typography>
              ))}
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Box sx={{ mt: 3, textAlign: 'center' }}>
