import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, Grid, Paper, Input, TextField, FormControl } from '@mui/material';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import html2canvas from 'html2canvas';

const Summary = () => {
  const [participants, setParticipants] = useState([]);
  const [foodItems, setFoodItems] = useState([]);
  const [totalAmounts, setTotalAmounts] = useState({});
  const [qrCodeFile, setQrCodeFile] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [paymentData, setPaymentData] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const savedParticipants = JSON.parse(localStorage.getItem('participants'));
    const savedFoodItems = JSON.parse(localStorage.getItem('foodItems'));
    const savedPaymentData = JSON.parse(localStorage.getItem('paymentData')) || {};

    if (savedParticipants) setParticipants(savedParticipants);
    if (savedFoodItems) {
      setFoodItems(savedFoodItems);
      calculateTotals(savedParticipants, savedFoodItems);
    }
    setPaymentData(savedPaymentData);
  }, []);

  const calculateTotals = (participants, items) => {
    const totals = {};
    participants.forEach(participant => {
      totals[participant] = { total: 0, foods: [] };
    });

    items.forEach(item => {
      const splitPrice = item.price / item.participants.length;
      item.participants.forEach(participant => {
        if (totals[participant]) {
          totals[participant].total += splitPrice;
          totals[participant].foods.push({ name: item.name, price: splitPrice });
        }
      });
    });

    setTotalAmounts(totals);
    localStorage.setItem('totalAmounts', JSON.stringify(totals));
  };

  const handleQrCodeChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setQrCodeFile(URL.createObjectURL(file));
      // Save QR Code to local storage
      setPaymentData(prev => ({ ...prev, qrCode: file }));
      localStorage.setItem('paymentData', JSON.stringify({ ...paymentData, qrCode: file }));
    }
  };

  const handlePromptPayChange = (participant, value) => {
    const newPaymentData = { ...paymentData, [participant]: { ...paymentData[participant], promptPay: value } };
    setPaymentData(newPaymentData);
    localStorage.setItem('paymentData', JSON.stringify(newPaymentData));
  };

  const handleBankAccountChange = (participant, value) => {
    const newPaymentData = { ...paymentData, [participant]: { ...paymentData[participant], bankAccount: value } };
    setPaymentData(newPaymentData);
    localStorage.setItem('paymentData', JSON.stringify(newPaymentData));
  };

  const saveSlipAsImage = () => {
    const slipElement = document.getElementById('summary-slip');
    html2canvas(slipElement).then(canvas => {
      const link = document.createElement('a');
      link.href = canvas.toDataURL('image/png');
      link.download = 'summary-slip.png';
      link.click();
    });
  };

  const handleBackClick = () => {
    navigate('/checkbill');
  };

  const handleStatusClick = () => {
    navigate('/statusdebtor');
  };

  const filteredParticipants = participants.filter(participant => 
    participant.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (Object.keys(totalAmounts).length === 0) {
    return (
      <Box sx={{ p: 3, textAlign: 'center', background: '#2E2E2E', borderRadius: '12px', color: '#ffffff' }}>
        <Typography variant="h5" sx={{ color: '#ffffff' }}>
          Please fill out the CheckBill information first.
        </Typography>
        <Button 
          variant="contained" 
          onClick={handleBackClick} 
          sx={{ mt: 2, backgroundColor: '#3f51b5', '&:hover': { backgroundColor: '#303f9f' } }}
        >
          Go to Check Bill
        </Button>
      </Box>
    );
  }

  return (
    <Box id="summary-slip" sx={{
      p: 3,
      background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
      borderRadius: '16px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.3)',
      maxWidth: '800px',
      mx: 'auto',
      mt: 4,
      position: 'relative',
      overflow: 'hidden',
      color: '#ffffff',
      animation: 'fadeIn 1s',
    }}>
      <Typography variant="h3" align="center" sx={{
        mb: 3,
        fontWeight: 'bold',
        textShadow: '0 0 10px rgba(233, 69, 96, 0.8)',
      }}>
        ðŸš€ Receipt Summary
      </Typography>

      <TextField 
        variant="outlined" 
        placeholder="Search Participants" 
        onChange={(e) => setSearchTerm(e.target.value)} 
        sx={{ mb: 3, width: '100%', backgroundColor: '#ffffff', borderRadius: '8px' }} 
      />

      <Grid container spacing={2} justifyContent="center">
        {filteredParticipants.map((participant, index) => (
          <Grid item xs={6} sm={4} md={3} key={index}>
            <motion.div 
              initial={{ opacity: 0, translateY: 20 }} 
              animate={{ opacity: 1, translateY: 0 }} 
              transition={{ duration: 0.5 }}
            >
              <Paper elevation={8} sx={{
                padding: 3,
                margin: '10px 0',
                borderRadius: '12px',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
                border: '2px solid #e94560',
                backdropFilter: 'blur(10px)',
                boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                transition: 'transform 0.3s, box-shadow 0.3s',
                '&:hover': {
                  transform: 'translateY(-5px)',
                  boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
                },
              }}>
                <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#ffffff' }}>
                  {participant}
                </Typography>
                <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#e94560' }}>
                  Total: {totalAmounts[participant]?.total.toFixed(2) || 0} à¸¿
                </Typography>
                <Typography variant="body1" sx={{ fontWeight: 'bold', marginBottom: 1, color: '#ffffff' }}>
                  Food Items:
                </Typography>
                {totalAmounts[participant]?.foods.map((food, foodIndex) => (
                  <Typography variant="body2" key={foodIndex} sx={{ pl: 2, color: '#ffffff' }}>
                    - {food.name}: {food.price.toFixed(2)} à¸¿
                  </Typography>
                ))}

                <FormControl fullWidth sx={{ marginTop: 2 }}>
                  <InputLabel>Payment Method</InputLabel>
                  <TextField
                    variant="outlined"
                    placeholder="PromptPay Number"
                    value={paymentData[participant]?.promptPay || ""}
                    onChange={(e) => handlePromptPayChange(participant, e.target.value)}
                    sx={{ marginTop: 1 }}
                  />
                  <TextField
                    variant="outlined"
                    placeholder="Bank Account Number"
                    value={paymentData[participant]?.bankAccount || ""}
                    onChange={(e) => handleBankAccountChange(participant, e.target.value)}
                    sx={{ marginTop: 1 }}
                  />
                </FormControl>
              </Paper>
            </motion.div>
          </Grid>
        ))}
      </Grid>

      <Box sx={{ mt: 3, textAlign: 'center' }}>
        <Typography variant="h6" sx={{ marginBottom: 2, fontWeight: 'bold', color: '#ffffff' }}>
          QR CODE
        </Typography>
        <Input type="file" accept="image/*" onChange={handleQrCodeChange} sx={{ display: 'none' }} id="qr-code-upload" />
        <label htmlFor="qr-code-upload">
          <Button variant="contained" component="span" sx={{ backgroundColor: '#e94560', '&:hover': { backgroundColor: '#d83b5c' } }}>
            Upload QR Code
          </Button>
        </label>
        {qrCodeFile && (
          <motion.img 
          src={qrCodeFile} 
          alt="QR Code" 
          style={{ maxWidth: '100%', marginTop: '10px', border: '2px solid #e94560', borderRadius: '8px', animation: 'fadeIn 1s' }} 
          initial={{ opacity: 0 }} 
          animate={{ opacity: 1 }} 
          transition={{ duration: 0.5 }}
        />
      )}
    </Box>

    <Box sx={{ mt: 3, display: 'flex', justifyContent: 'center', gap: 2 }}>
      <Button 
        variant="contained" 
        color="primary" 
        onClick={saveSlipAsImage} 
        sx={{ backgroundColor: '#e94560', '&:hover': { backgroundColor: '#d83b5c' } }}
      >
        Save as Image
      </Button>
      <Button 
        variant="contained" 
        onClick={handleBackClick} 
        sx={{ backgroundColor: '#3f51b5', '&:hover': { backgroundColor: '#303f9f' } }}
      >
        Back to Check Bill
      </Button>
      <Button 
        variant="contained" 
        onClick={handleStatusClick} 
        sx={{ backgroundColor: '#f50057', '&:hover': { backgroundColor: '#c51162' } }}
      >
        Go to Status Debtor
      </Button>
    </Box>
  </Box>
);
};

export default Summary;
