import React, { useState, useEffect } from "react";
import {
  TextField,
  Button,
  List,
  ListItem,
  Checkbox,
  Typography,
  Grid,
  Paper,
  Chip,
  Box,
  Tooltip,
  IconButton,
} from "@mui/material";
import { motion } from "framer-motion";
import DeleteIcon from "@mui/icons-material/Delete";
import AddIcon from "@mui/icons-material/Add";
import RestaurantMenuIcon from "@mui/icons-material/RestaurantMenu";
import PersonIcon from "@mui/icons-material/Person";
import { useSnackbar } from "notistack";
import { v4 as uuidv4 } from "uuid"; // For unique IDs

const loadFromLocalStorage = (key) => {
  const data = localStorage.getItem(key);
  return data ? JSON.parse(data) : [];
};

const saveToLocalStorage = (key, data) => {
  localStorage.setItem(key, JSON.stringify(data));
};

const CheckBill = () => {
  const { enqueueSnackbar } = useSnackbar();
  const [participants, setParticipants] = useState(loadFromLocalStorage("participants"));
  const [participantName, setParticipantName] = useState("");
  const [foodItems, setFoodItems] = useState(loadFromLocalStorage("foodItems"));
  const [foodName, setFoodName] = useState("");
  const [foodPrice, setFoodPrice] = useState("");
  const [selectedParticipants, setSelectedParticipants] = useState({});

  useEffect(() => {
    saveToLocalStorage("participants", participants);
    saveToLocalStorage("foodItems", foodItems);
  }, [participants, foodItems]);

  const handleAddParticipant = () => {
    if (participantName.trim() && !participants.includes(participantName.trim())) {
      setParticipants([...participants, participantName.trim()]);
      setParticipantName("");
      enqueueSnackbar("Participant added!", { variant: "success" });
    } else {
      enqueueSnackbar("Participant name is empty or already exists.", { variant: "error" });
    }
  };

  const handleRemoveParticipant = (index) => {
    setParticipants(participants.filter((_, i) => i !== index));
    enqueueSnackbar("Participant removed!", { variant: "warning" });
  };

  const handleResetParticipants = () => {
    setParticipants([]);
    enqueueSnackbar("All participants reset!", { variant: "info" });
  };

  const handleAddFoodItem = () => {
    if (foodName && foodPrice > 0 && Object.keys(selectedParticipants).length > 0) {
      const splitPrice = (parseFloat(foodPrice) / Object.keys(selectedParticipants).length).toFixed(2);
      setFoodItems([
        ...foodItems,
        {
          id: uuidv4(),
          name: foodName,
          price: parseFloat(foodPrice),
          participants: Object.keys(selectedParticipants),
          splitPrice,
        },
      ]);
      setFoodName("");
      setFoodPrice("");
      setSelectedParticipants({});
      enqueueSnackbar("Food item added!", { variant: "success" });
    } else {
      enqueueSnackbar("Please fill all fields correctly.", { variant: "error" });
    }
  };

  const handleRemoveFoodItem = (id) => {
    setFoodItems(foodItems.filter((item) => item.id !== id));
    enqueueSnackbar("Food item removed!", { variant: "warning" });
  };

  const handleSelectParticipant = (name) => {
    setSelectedParticipants((prev) => ({
      ...prev,
      [name]: !prev[name],
    }));
  };

  const totalSummary = foodItems.reduce((acc, food) => {
    food.participants.forEach((participant) => {
      acc[participant] = (acc[participant] || 0) + parseFloat(food.splitPrice);
    });
    return acc;
  }, {});

  return (
    <Box p={3} sx={{ backgroundColor: "#f9f9f9" }}>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <Typography variant="h4" align="center" gutterBottom color="primary">
          Check Bill
        </Typography>
      </motion.div>

      {/* Add Participants Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}>
          <PersonIcon /> Add Participants
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={8}>
            <TextField
              label="Participant Name"
              value={participantName}
              onChange={(e) => setParticipantName(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={4}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleAddParticipant}
              startIcon={<AddIcon />}
              fullWidth
              sx={{ borderRadius: "20px" }}
            >
              Add
            </Button>
          </Grid>
        </Grid>
        <Box sx={{ mt: 2 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              sx={{
                m: 0.5,
                backgroundColor: "#81d4fa",
                color: "white",
                "&:hover": {
                  backgroundColor: "#40c4ff",
                },
                borderRadius: "15px",
              }}
              onDelete={() => handleRemoveParticipant(index)}
            />
          ))}
        </Box>
        <Button
          variant="outlined"
          color="secondary"
          onClick={handleResetParticipants}
          fullWidth
          sx={{ mt: 2, borderRadius: "20px" }}
        >
          Reset All Participants
        </Button>
      </Paper>

      {/* Add Food Items Section */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}>
          <RestaurantMenuIcon /> Add Food Items
        </Typography>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={6}>
            <TextField
              label="Food Name"
              value={foodName}
              onChange={(e) => setFoodName(e.target.value)}
              fullWidth
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Price (Baht)"
              type="number"
              value={foodPrice}
              onChange={(e) => setFoodPrice(e.target.value)}
              fullWidth
              inputProps={{ min: 0 }} // Prevent negative prices
            />
          </Grid>
        </Grid>
        <Typography variant="subtitle1" sx={{ mt: 2 }}>
          Select Participants to Split the Bill:
        </Typography>
        <Box sx={{ display: "flex", flexWrap: "wrap", mt: 1 }}>
          {participants.map((name, index) => (
            <Chip
              key={index}
              label={name}
              variant={selectedParticipants[name] ? "filled" : "outlined"}
              onClick={() => handleSelectParticipant(name)}
              sx={{
                m: 0.5,
                backgroundColor: selectedParticipants[name] ? "#ffccbc" : "white",
                borderColor: "rgba(0, 0, 0, 0.12)",
                "&:hover": {
                  backgroundColor: selectedParticipants[name] ? "#ffab40" : "rgba(0, 0, 0, 0.04)",
                },
                borderRadius: "15px",
              }}
            />
          ))}
        </Box>
        <Button
          variant="contained"
          color="primary"
          onClick={handleAddFoodItem}
          startIcon={<AddIcon />}
          fullWidth
          sx={{ mt: 2, borderRadius: "20px" }}
        >
          Add Food Item
        </Button>
      </Paper>

      {/* Food Items List */}
      <Paper elevation={3} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}>
          Food Items List
        </Typography>
        <List>
          {foodItems.map((item) => (
            <ListItem
              key={item.id}
              sx={{
                justifyContent: "space-between",
                backgroundColor: "white",
                borderRadius: "10px",
                mb: 1,
                boxShadow: 1,
                p: 1,
              }}
            >
              <Box>
                <Typography variant="body1" sx={{ fontWeight: "bold" }}>
                  {item.name} - {item.price.toFixed(2)} Baht
                </Typography>
                <Typography variant="body2" sx={{ fontStyle: "italic" }}>
                  Split among: {item.participants.join(", ")}
                </Typography>
                <Typography variant="body2" sx={{ color: "grey" }}>
                  {item.splitPrice} Baht each
                </Typography>
              </Box>
              <IconButton onClick={() => handleRemoveFoodItem(item.id)} color="error">
                <DeleteIcon />
              </IconButton>
            </ListItem>
          ))}
        </List>
      </Paper>

      {/* Summary Section */}
      <Paper elevation={3} sx={{ p: 2 }}>
        <Typography variant="h6" sx={{ mb: 2, color: "#d32f2f" }}>
          Summary
        </Typography>
        {Object.keys(totalSummary).length > 0 ? (
          <List>
            {Object.entries(totalSummary).map(([participant, total]) => (
              <ListItem key={participant} sx={{ justifyContent: "space-between" }}>
                <Typography variant="body1" sx={{ fontWeight: "bold" }}>
                  {participant}
                </Typography>
                <Typography variant="body1" sx={{ color: "#d32f2f" }}>
                  {total.toFixed(2)} Baht
                </Typography>
              </ListItem>
            ))}
          </List>
        ) : (
          <Typography variant="body1" sx={{ color: "grey" }}>
            No expenses yet.
          </Typography>
        )}
      </Paper>
    </Box>
  );
};

export default CheckBill;
